catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
   var vendor_name = g_form.getValue('vendor_name');
   var cert_format = g_form.getValue('push_certificate_format');
   if (vendor_name == "LinuxServer") {
    if (cert_format == 'PEM(*.pem)' || cert_format == 'PEM(*.crt)' || cert_format == 'PEM(*.cer)' || cert_format == 'DER(*.der)' || cert_format == 'DER(*.cer)') {
        g_form.clearValue('keystore_location');
		g_form.setMandatory('keystore_location', false);
        g_form.setDisplay('keystore_location', false);
        g_form.clearValue('keystore_password');
		g_form.setMandatory('keystore_password', false);
        g_form.setDisplay('keystore_password', false);
		g_form.clearValue('push_root_and_intermediate_certificates');
        g_form.setDisplay('push_root_and_intermediate_certificates', true);
        g_form.clearValue('certificate_directory');
        g_form.setDisplay('certificate_directory', true);
        g_form.setMandatory('certificate_directory', true);
        g_form.clearValue('key_directory');
        g_form.setDisplay('key_directory', true);
        g_form.setMandatory('key_directory', true);
    } else {
		g_form.clearValue('certificate_directory');
		g_form.setMandatory('certificate_directory', false);
        g_form.setDisplay('certificate_directory', false);
        g_form.clearValue('key_directory');
		g_form.setMandatory('key_directory', false);
        g_form.setDisplay('key_directory', false);
		g_form.setValue('private_key_in_device', false, false);
		g_form.setDisplay('push_root_and_intermediate_certificates', false);
        g_form.clearValue('keystore_location');
        g_form.setDisplay('keystore_location', true);
        g_form.setMandatory('keystore_location', true);
        g_form.clearValue('keystore_password');
        g_form.setDisplay('keystore_password', true);
        g_form.setMandatory('keystore_password', true);
    }
}

}
