catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var common_name = g_form.getValue('common_name');
    var dns = g_form.getValue('dns');
	var san_email = g_form.getValue('san_email');
    var ip_address = g_form.getValue('ip_address');
    g_form.clearValue('ip_address');
    var san = '';
    if (g_form.getValue('add_ip_address') === 'false') {
        if (g_form.getValue('certificate_category') === 'Server' || g_form.getValue('certificate_authority') === 'Microsoft Enterprise') {
            if (common_name !== '') {
                san = common_name;
            }
            if (dns !== '') {
                if (san !== '') {
                    san = san + ",";
                }
                san = san + dns;
            }
            if (san_email !== '') {
                if (san !== '') {
                    san = san + ",";
                }
                san = san + san_email;
            }
            g_form.setValue('subject_alternative_name', san, san);
        }
    }
}
