catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    try {
        var CA = g_form.getValue('certificate_authority');
        var region_name = g_form.getValue('region');
        var amazon_ca_details = g_form.getValue('ejbca_ca_details');
        g_form.clearOptions('issuer');
        g_form.setDisplay('issuer', true);
        g_form.setMandatory('issuer', true);
        g_form.addOption('issuer', '', 'Select');

        try {
            amazon_ca_details = JSON.parse(amazon_ca_details);
        } catch (err) {
            throw "Couldn't parse the ejbca ca details to fetch the specific fields.";
        }
        amazon_ca_details = amazon_ca_details.issuerDetails;
        var issuer_flag = 0;
        for (var amazon_issuer = 0; amazon_issuer < amazon_ca_details.length; amazon_issuer++) {
            if (amazon_ca_details[amazon_issuer].region === region_name) {
                var issuer = amazon_ca_details[amazon_issuer].certificateAuthorities;
                for (var iterator = 0; iterator < issuer.length; iterator++) {
                    if (issuer[iterator].selected === true) {
                        g_form.addOption('issuer', issuer[iterator].commonName, issuer[iterator].commonName);
                        issuer_flag = 1;
                    }
                }
                break;
            }
        }
        if (issuer_flag === 1) {
            g_form.setValue('issuer', 'Select');
        } else {
            throw "There is no issuer CA configured for the selected region.";
        }
        g_form.setDisplay('csr_input_method', true);
        g_form.setMandatory('csr_input_method', true);
    } catch (error) {
        g_form.addErrorMessage("Error: " + error);
        var message = 'Error occured while fetching key type from policy details - ' + error;
        var ga = new GlideAjax('avx_logger');
        ga.addParam("sysparm_name", "avxLogger");
        ga.addParam("sysparm_level", "Error");
        ga.addParam("sysparm_script", "set_bit_length");
        ga.addParam("sysparm_application", "AppViewX");
        ga.addParam("sysparm_message", message);
        ga.getXML(function() {});
    }
}
