catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var CA = g_form.getValue('certificate_authority');
    var common_name = g_form.getValue('common_name');
    var san_email = g_form.getValue('san_email');
    var dns = g_form.getValue('dns');
    if (CA === 'Microsoft Enterprise') {
        if (!dns.match(/^[A-Za-z0-9*._-]*$/) && !dns.match(/^[^-\s][A-Za-z0-9\s\@\*\_\.\-\,]+$/)) {
            alert("No special characters except ('*', '.','-', '_', '@') and spaces are allowed value should not start with spaces.");
            g_form.clearValue('dns');
        }
    }
    if (CA === 'Amazon') {
        if (!dns.match(/^(\*\.)?(((?!-)[A-Za-z0-9-]{0,62}[A-Za-z0-9])\.)+((?!-)[A-Za-z0-9-]{1,62}[A-Za-z0-9])+((\,(\*\.)?(((?!-)[A-Za-z0-9-]{0,62}[A-Za-z0-9])\.)+((?!-)[A-Za-z0-9-]{1,62}[A-Za-z0-9])+)?)+$/)) {
            alert("Subject alternative names may start with (*.). No special characters except ('.','-') are allowed in domain name. Use, for multiple entries.");
            g_form.clearValue('dns');
        }
    }
	if (CA === 'Trustwave') {
		if (!dns.match(/^[A-Za-z0-9.\-]*$/)) {
			alert("No special characters except ('.','-') are allowed.");
			g_form.clearValue('dns');
		}
	}
    dns = g_form.getValue('dns');
    var ip_address = g_form.getValue('ip_address');
    var san = '';
    if (g_form.getValue('certificate_category') === 'Server' || CA === 'Microsoft Enterprise') {
        if (common_name !== '') {
            san = common_name;
        }
        if (dns !== '') {
            if (san !== '') {
                san = san + ",";
            }
            san = san + dns;
        }
        if (ip_address !== '') {
            if (san !== '') {
                san = san + ",";
            }
            san = san + ip_address;
        }
        if (san_email !== '') {
            if (san !== '') {
                san = san + ",";
            }
            san = san + san_email;
        }
        g_form.setValue('subject_alternative_name', san, san);
    }

}
