catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var push_root_and_intermediate = g_form.getValue('push_root_and_intermediate_certificates');
    var vendor_name = g_form.getValue('vendor_name');
    var private_key_in_device = g_form.getValue('private_key_in_device');
    var customize_push_location = g_form.getValue('customize_push_location');
    if ((vendor_name == 'Apache'|| vendor_name == 'Tomcat') && push_root_and_intermediate == 'true') {
        if (private_key_in_device == 'true' || customize_push_location == 'true') {
			g_form.clearValue('intermediate_and_ca_certificate_directory');
            g_form.setDisplay('intermediate_and_ca_certificate_directory', true);
            g_form.setMandatory('intermediate_and_ca_certificate_directory', true);
        } else {
			g_form.clearValue('intermediate_and_ca_certificate_directory');
            g_form.setMandatory('intermediate_and_ca_certificate_directory', false);
            g_form.setDisplay('intermediate_and_ca_certificate_directory', false);
        }
    } else if (vendor_name == "LinuxServer" && push_root_and_intermediate == 'true') {
            g_form.clearValue('intermediate_and_ca_certificate_directory');
            g_form.setDisplay('intermediate_and_ca_certificate_directory', true);
            g_form.setMandatory('intermediate_and_ca_certificate_directory', true);
	}else {
		g_form.clearValue('intermediate_and_ca_certificate_directory');
        g_form.setMandatory('intermediate_and_ca_certificate_directory', false);
        g_form.setDisplay('intermediate_and_ca_certificate_directory', false);
    }

}
