catalog_script_client.script:
/*
Function to populate the below details by dynamically fetching it from the AppviewX policy..
	1. Sectigo Server type
*/
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    function setServerType(servers) {
        try {
            var certificate_category = g_form.getValue('certificate_category');
            if (certificate_category != 'Server') {
                g_form.setMandatory('sectigo_server_type', false);
                g_form.setDisplay('sectigo_server_type', false);
            }
            if (servers.length > 0) {
                g_form.setDisplay('sectigo_server_type', true);
                g_form.setMandatory('sectigo_server_type', true);
                g_form.clearOptions('sectigo_server_type');
                g_form.clearValue('sectigo_server_type');
                g_form.addOption('sectigo_server_type', '', 'Select');
                for (var server = 0; server < servers.length; server++) {
                    g_form.addOption('sectigo_server_type', servers[server], servers[server]);
                }
                g_form.setValue('sectigo_server_type', '', 'Select');
            }
        } catch (error) {
            g_form.addErrorMessage("Error : " + error);
            var message = 'Error occurred while fetching sectigo server types from AppViewX - ' + error;
            var ga = new GlideAjax('avx_logger');
            ga.addParam("sysparm_name", "avxLogger");
            ga.addParam("sysparm_level", "Error");
            ga.addParam("sysparm_script", "set_server_type");
            ga.addParam("sysparm_application", "AppViewX");
            ga.addParam("sysparm_message", message);
            ga.getXML(function() {});
        }
    }

    function getResponse(response) {
        try {
            var answer = response.responseXML.documentElement.getAttribute('answer');
            try {
                answer = JSON.parse(answer);
            } catch (err) {
                throw "Couldn't parse Server Type details.";
            }
            var server_response = answer.response;
            var status = server_response.output.status;
            if (status != "Success") {
                throw "Could not find Server Type for the selected input.";
            }
            var servers = server_response.output.vendor_response;
            setServerType(servers);
        } catch (error) {
            g_form.addErrorMessage("Error : " + error);
            var message = 'Error occurred while fetching sectigo server types from AppViewX - ' + error;
            var ga = new GlideAjax('avx_logger');
            ga.addParam("sysparm_name", "avxLogger");
            ga.addParam("sysparm_level", "Error");
            ga.addParam("sysparm_script", "set_server_type");
            ga.addParam("sysparm_application", "AppViewX");
            ga.addParam("sysparm_message", message);
            ga.getXML(function() {});
        }
    }
    try {
        var CA = g_form.getValue('certificate_authority');
        var ca_account = g_form.getValue('ca_account');
        var ca_policy = g_form.getValue('ca_policy_details');
        var certificate_policy = g_form.getValue('certificate_policy');
        var certificate_category = g_form.getValue('certificate_category');
        var division_type = g_form.getValue('division');
        var certificate_type = g_form.getValue('sectigo_certificate_type');
        try {
            ca_policy = JSON.parse(ca_policy);
        } catch (err) {
            throw "Couldn't parse CA policy details.";
        }
        if (CA === 'Comodo Certificate Manager') {
            g_form.setMandatory('validity_unit', true);
            g_form.setDisplay('validity_unit', true);
            g_form.clearOptions('validity_unit');
            g_form.addOption('validity_unit', '', 'Select');
            for (var iterator = 0; iterator < ca_policy.length; iterator++) {
                var result = ca_policy[iterator];
                if (!result.certificateAuthority) {
                    throw 'Certificate Authority Details is not configured in AppViewX Policy.';
                }
                if (result.certificateAuthority === CA) {
                    if (!result.caSettings) {
                        throw 'CA Account settings is not configured in AppViewX Policy.';
                    }
                    var sectigo_settings = result.caSettings;
                    for (var sec = 0; sec < sectigo_settings.length; sec++) {
                        if (sectigo_settings[sec].name === ca_account) {
                            if (!sectigo_settings[sec].certTypes) {
                                throw 'Sectigo certificate types are not configured in AppviewX Policy.';
                            }
                            var cert_types = sectigo_settings[sec].certTypes;
                            for (var cert_type = 0; cert_type < cert_types.length; cert_type++) {
                                if ((cert_types[cert_type].type).toLowerCase() === certificate_category.toLowerCase() && cert_types[cert_type].name === certificate_type) {
                                    if (certificate_category === 'Server') {
                                        var server = cert_types[cert_type].vendorSpecificDetails;
                                        if (!server) {
											var company_name = g_form.getValue('company_name');
                                            var ajax = new GlideAjax('get_server_types');
                                            ajax.addParam('sysparm_name', 'getServerTypes');
                                            ajax.addParam('certificate_authority', CA);
                                            ajax.addParam('ca_settings_name', ca_account);
                                            ajax.addParam('certificate_type', certificate_type);
                                            ajax.addParam('certificate_category', certificate_category);
                                            ajax.addParam('cert_policy', certificate_policy);
											ajax.addParam('sysparm_company', company_name);
                                            ajax.getXML(getResponse);
                                        } else {
                                            // if (!server.serverType) {
                                            //     throw 'Server type is not configured in AppviewX Policy.';
                                            // }
                                            var servers = server.serverType || [];
                                            setServerType(servers);
                                        }
                                    }
                                    var keys = Object.keys(cert_types[cert_type].validityPeriods);
                                    for (var v_unit = 0; v_unit < keys.length; v_unit++) {
										var unit_length = cert_types[cert_type].validityPeriods[keys[v_unit]];
                                        if (keys[v_unit].includes("Years") && unit_length > 0) {
                                            g_form.addOption('validity_unit', "Years", "Years");
                                        } else if (keys[v_unit].includes("Months") && unit_length > 0) {
                                            g_form.addOption('validity_unit', "Months", "Months");
                                        } else if (keys[v_unit].includes("Days") && unit_length > 0) {
                                            g_form.addOption('validity_unit', "Days", "Days");
                                        }

                                    }
                                    g_form.setValue('validity_unit', '', 'Select');
                                }
                            }
                            break;
                        }
                    }
                }
            }
        }
    } catch (error) {
        g_form.addErrorMessage("Error: " + error + " Please check with the AppviewX admin/CLM Settings.");
        var message = 'Error occurred while setting server type in the form - ' + error;
        var ga = new GlideAjax('avx_logger');
        ga.addParam("sysparm_name", "avxLogger");
        ga.addParam("sysparm_level", "Error");
        ga.addParam("sysparm_script", "set_server_type");
        ga.addParam("sysparm_application", "AppViewX");
        ga.addParam("sysparm_message", message);
        ga.getXML(function() {});
    }

}
