catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
	var certificate_category = g_form.getValue('certificate_category');
	var certificate_authority = g_form.getValue('certificate_authority');
	var common_name = g_form.getValue('common_name');
	var company_name = g_form.getValue('company_name');
    var ajax = new GlideAjax('validate_csr_attachment');
    ajax.addParam('sysparm_name', 'checkAttachmentSizeAndType');
    ajax.addParam('sysparm_existing', newValue);
	ajax.addParam('certificate_category', certificate_category);
	ajax.addParam('certificate_authority', certificate_authority);
	ajax.addParam('common_name', common_name);
	ajax.addParam('sysparm_company', company_name);
	ajax.getXML(getRITMdata);

    function getRITMdata(response) {
		try {
			g_form.clearValue('parsed_csr_values');
			g_form.setDisplay('parsed_csr_values', true);
			g_form.setMandatory('allow_new_common_name',false);
			g_form.setDisplay('allow_new_common_name', false);
			var res = response.responseXML.documentElement.getAttribute('answer');
			try {
				res = JSON.parse(res);
				g_form.setValue("parsed_csr_values", res.csr_parameters);
				if(res.cn_validity){
					g_form.clearValue('allow_new_common_name');
					g_form.setMandatory('allow_new_common_name', true);
			        g_form.setDisplay('allow_new_common_name', true);
				}
			} catch (err) {
				throw res || "Error while parsing the given CSR";
			}
			// if (answer) {
			// 	alert(answer);
			// 	g_form.clearValue("paste_csr");
			// }
		} catch (error) {
			alert(error);
			g_form.clearValue("file_upload");
		}
    }

}
