catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    try {
		var iterator;
        var CA = g_form.getValue('certificate_authority');
        var AppViewX = ["Key compromised", "CA compromised", "Privilege withdrawn"];
        var entrust = ["Superseded", "Cessation of operation", "Affiliation Changed", "Key compromised"];
        var ejbca = ["Key compromise", "CA compromise", "Affiliation Changed", "Superseded", "Cessation of operation", "Privilege withdrawn", "Unspecified", "AA compromise"];
        var Microsoft_Enterprise = ["Key compromise", "CA compromise", "Affiliation Changed", "Superseded", "Cessation of operation"];
		var GoDaddy = ["Key compromise", "Affiliation Changed", "Superseded", "Cessation of operation", "Privilege Withdrawn"];
		var pki = [ "Key compromise", "Affiliation Changed", "Unspecified", "CA compromise", "Attribute authority compromise", "Superseded", "Cessation of operation", "Privilege withdrawn"];
		var hydrantId = ["Unspecified", "Key compromised", "Cessation of operation", "Affiliation Changed", "Superseded"];
		var swisssign = ["Superseded", "Key compromise", "Affiliation changed", "Cessation of operation", "Unused"];
		var digicert = ["Key compromise", "Affiliation Changed", "Superseded", "Cessation of operation"];
        g_form.setDisplay('reason', true);
        g_form.setDisplay('comments', true);
        g_form.clearValue('comments');
        g_form.clearOptions('reason');
        g_form.addOption('reason', '', 'Select');
        if (CA == "Entrust") {
            for (iterator = 0; iterator < entrust.length; iterator++) {
                g_form.addOption('reason', entrust[iterator], entrust[iterator]);
            }
        } else if (CA == "EJBCA") {
            for (iterator = 0; iterator < ejbca.length; iterator++) {
                g_form.addOption('reason', ejbca[iterator], ejbca[iterator]);
            }
        } else if (CA == "Microsoft Enterprise") {
            for (iterator = 0; iterator < Microsoft_Enterprise.length; iterator++) {
                g_form.addOption('reason', Microsoft_Enterprise[iterator], Microsoft_Enterprise[iterator]);
            }
        } else if (CA == "Microsoft Standalone") {
            for (iterator = 0; iterator < Microsoft_Enterprise.length; iterator++) {
                g_form.addOption('reason', Microsoft_Enterprise[iterator], Microsoft_Enterprise[iterator]);
            }
        } else if (CA == "GoDaddy") {
            for (iterator = 0; iterator < GoDaddy.length; iterator++) {
                g_form.addOption('reason', GoDaddy[iterator], GoDaddy[iterator]);
            }
        } else if (CA == "AppViewX PKIaaS") {
            for (iterator = 0; iterator < pki.length; iterator++) {
                g_form.addOption('reason', pki[iterator], pki[iterator]);
            }
        } else if (CA == "HydrantID") {
            for (iterator = 0; iterator < hydrantId.length; iterator++) {
                g_form.addOption('reason', hydrantId[iterator], hydrantId[iterator]);
            }
        } else if (CA == "SwissSign") {
            for (iterator = 0; iterator < swisssign.length; iterator++) {
                g_form.addOption('reason', swisssign[iterator], swisssign[iterator]);
            }
        } else if (CA == "DigiCert") {
            for (iterator = 0; iterator < digicert.length; iterator++) {
                g_form.addOption('reason', digicert[iterator], digicert[iterator]);
            }
        } else {
            for (iterator = 0; iterator < AppViewX.length; iterator++) {
                g_form.addOption('reason', AppViewX[iterator], AppViewX[iterator]);
            }
        }

    } catch (error) {
        g_form.addErrorMessage("Error: " + error);
        var message = 'Error occurred while setting revoke reasons in form - ' + error;
        var ga = new GlideAjax('avx_logger');
        ga.addParam("sysparm_name", "avxLogger");
        ga.addParam("sysparm_level", "Error");
        ga.addParam("sysparm_script", "revoke_set_reason_based_on_CA");
        ga.addParam("sysparm_application", "AppViewX");
        ga.addParam("sysparm_message", message);
        ga.getXML(function() {});
    }
}
