catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    try {
        var CA = g_form.getValue('certificate_authority');
        var ca_account = g_form.getValue('ca_account');
        var ca_policy = g_form.getValue('ca_policy_details');
		var csr_input_method = g_form.getValue('csr_input_method');
        var certificate_policy = g_form.getValue('certificate_policy');
        var certificate_category = g_form.getValue('certificate_category');
        var swisssign_client_name = g_form.getValue('swisssign_client_name');
        var swisssign_product = g_form.getValue('swisssign_product');
        try {
            ca_policy = JSON.parse(ca_policy);
        } catch (err) {
            throw "Couldn't parse CA policy details.";
        }
        if (CA === 'SwissSign') {
            g_form.setMandatory('validity_unit', true);
            g_form.setDisplay('validity_unit', true);
            g_form.clearOptions('validity_unit');
            g_form.addOption('validity_unit', '', 'Select');
            if (csr_input_method === 'Manual') {
                g_form.setMandatory('hash_function', true);
                g_form.setDisplay('hash_function', true);
                g_form.setMandatory('key_type', true);
                g_form.setDisplay('key_type', true);
                g_form.setMandatory('bit_length', true);
                g_form.setDisplay('bit_length', true);
                g_form.addOption('key_type', '', 'Select');
                g_form.setValue('key_type', '', 'Select');
                g_form.addOption('hash_function', '', 'Select');
                g_form.setValue('hash_function', '', 'Select');
            }
            for (var iterator = 0; iterator < ca_policy.length; iterator++) {
                var result = ca_policy[iterator];
                if (!result.certificateAuthority) {
                    throw 'Certificate Authority Details is not configured in AppViewX Policy.';
                }
                if (result.certificateAuthority === CA) {
                    if (!result.caSettings) {
                        throw 'CA Account settings is not configured in AppViewX Policy';
                    }
                    var swisssign_ca_settings = result.caSettings;
                    for (var div = 0; div < swisssign_ca_settings.length; div++) {
                        if (swisssign_ca_settings[div].name === ca_account) {
                            if (!swisssign_ca_settings[div].clients) {
                                throw 'Client details are missing in the policy.';
                            }
                            var swisssign_clients = swisssign_ca_settings[div].clients;
                            for (var client = 0; client < swisssign_clients.length; client++) {
                                if (swisssign_clients[client].name === swisssign_client_name) {
                                    if (!swisssign_clients[client].certTypes) {
                                        throw 'Certificate types are not configured in AppviewX Policy.';
                                    }
                                    var cert_types = swisssign_clients[client].certTypes;
                                    for (var cert_type = 0; cert_type < cert_types.length; cert_type++) {
                                        if (cert_types[cert_type].name === swisssign_product) {
                                            if(!cert_types[cert_type].shaType) {
                                                throw "ShaType is not configured in AppViewX Policy.";
                                            }
                                            var flag = 0;
                                            if (csr_input_method === 'Manual') {
                                                var hash_type = cert_types[cert_type].shaType;
                                                for (var j = 0; j < hash_type.length; j++) {
                                                    g_form.addOption('hash_function', hash_type[j], hash_type[j]);
                                                    if (flag == 0) {
                                                        g_form.setValue('hash_function', hash_type[j], hash_type[j]);
                                                    }
                                                    flag = 1;
                                                }
                                                flag = 0;
                                                if(!cert_types[cert_type].certificateKeyParams) {
                                                    throw 'KeyParameters are not configured in AppviewX Policy.';
                                                }
                                                var cert_params = cert_types[cert_type].certificateKeyParams;
                                                var certparam_keys = Object.keys(cert_params);
                                                for (var j = 0; j < certparam_keys.length; j++) {
                                                    if (cert_params[certparam_keys[j]].length > 0) {
                                                        g_form.addOption('key_type', certparam_keys[j], certparam_keys[j]);
                                                        if (flag == 0) {
                                                            g_form.setValue('key_type', certparam_keys[j], certparam_keys[j]);
                                                        }
                                                        flag = 1;
                                                    }
                                                }
                                            }
                                            flag = 0;
                                            var validity = cert_types[cert_type].validityPeriods;
                                            var keys = Object.keys(cert_types[cert_type].validityPeriods);
                                            for (var v_unit = 0; v_unit < keys.length; v_unit++) {
                                                if (keys[v_unit].includes("Years") && validity[keys[v_unit]] || [].length > 0) {
                                                    g_form.addOption('validity_unit', "Years", "Years");
                                                } else if (keys[v_unit].includes("Months") && validity[keys[v_unit]].length > 0) {
                                                    g_form.addOption('validity_unit', "Months", "Months");
                                                } else if (keys[v_unit].includes("Days") && validity[keys[v_unit]].length > 0) {
                                                    g_form.addOption('validity_unit', "Days", "Days");
                                                }
                                            }
                                            g_form.setValue('validity_unit', '', 'Select');
                                        }
                                    }
                                }
                                break;
                            }
                        }
                        break;
                    }
                }
            }
        }
    } catch (error) {
        g_form.addErrorMessage("Error: " + error + " Please check with the AppviewX admin/CLM Settings.");
        var message = 'Error occurred while setting hash type/bitlength/validity for the SwissSign CA in the form - ' + error;
        var ga = new GlideAjax('avx_logger');
        ga.addParam("sysparm_name", "avxLogger");
        ga.addParam("sysparm_level", "Error");
        ga.addParam("sysparm_script", "set_swisssign_csr_details");
        ga.addParam("sysparm_application", "AppViewX");
        ga.addParam("sysparm_message", message);
        ga.getXML(function() {});
    }

}
