catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    try {
        var CA = g_form.getValue('certificate_authority');
        var end_entity_profile_name = g_form.getValue('ejbca_end_entity_profile_name');
        var ejbca_ca_details = g_form.getValue('ejbca_ca_details');
        g_form.clearOptions('ejbca_issuer_common_name');
        g_form.setDisplay('ejbca_issuer_common_name', true);
        g_form.setMandatory('ejbca_issuer_common_name', true);
        g_form.addOption('ejbca_issuer_common_name', '', "Select");
        g_form.setValue('ejbca_issuer_common_name', '', 'Select');
        g_form.clearOptions('ejbca_certificate_profile_name');
        g_form.setDisplay('ejbca_certificate_profile_name', true);
        g_form.setMandatory('ejbca_certificate_profile_name', true);
        g_form.addOption('ejbca_certificate_profile_name', '', "Select");
        g_form.setValue('ejbca_certificate_profile_name', '', 'Select');
        try {
            ejbca_ca_details = JSON.parse(ejbca_ca_details);
        } catch (err) {
            throw "Couldn't parse the ejbca ca details to fetch the specific fields.";
        }
        ejbca_ca_details = ejbca_ca_details.endEntityProfiles;
        for (var ejbca_profile = 0; ejbca_profile < ejbca_ca_details.length; ejbca_profile++) {
            if (end_entity_profile_name === ejbca_ca_details[ejbca_profile].profileName) {
                var caNames = ejbca_ca_details[ejbca_profile].caNames;
                if (caNames.length < 1) {
                    g_form.addErrorMessage("There is no EJBCA Issuer Common Name configured for the selected EJBCA End Entity Profile Name");
                }
                for (var issuer = 0; issuer < caNames.length; issuer++) {
                    g_form.addOption('ejbca_issuer_common_name', caNames[issuer], caNames[issuer]);
                }
                var certificateProfilesNames = ejbca_ca_details[ejbca_profile].certificateProfilesNames;
                if (certificateProfilesNames.length < 1) {
                    g_form.addErrorMessage("There is no EJBCA Certificate Profile Name configured for the selected EJBCA End Entity Profile Name");
                }
                for (var name = 0; name < certificateProfilesNames.length; name++) {
                    g_form.addOption('ejbca_certificate_profile_name', certificateProfilesNames[name], certificateProfilesNames[name]);
                }
                break;
            }
        }
    } catch (error) {
        g_form.addErrorMessage("Error: " + error);
        var message = 'Error occured while fetching key type from policy details - ' + error;
        var ga = new GlideAjax('avx_logger');
        ga.addParam("sysparm_name", "avxLogger");
        ga.addParam("sysparm_level", "Error");
        ga.addParam("sysparm_script", "set_bit_length");
        ga.addParam("sysparm_application", "AppViewX");
        ga.addParam("sysparm_message", message);
        ga.getXML(function() {});
    }
}
