catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var subject_alternative_name = g_form.getValue('subject_alternative_name');
    var certificate_category = g_form.getValue('certificate_category');
    var CA = g_form.getValue('certificate_authority');
    if (certificate_category.toLowerCase() === 'client') {
        if (CA === 'AppViewX' || CA === 'AppViewX PKIaaS') {
            if (!subject_alternative_name.match(/^([\w+-.%]+@[\w.-]+\.[A-Za-z]{2,4})((,){1}[\w+-.%]+@[\w.-]+\.[A-Za-z]{2,4})*$/)) {
                alert("For Client Certificates the subject alternative name should be a valid EMAIL ADDRESS");
                g_form.clearValue('subject_alternative_name');
            }
        }
        else if (CA === 'DigiCert') {
            if (!subject_alternative_name.match(/^[^-\s][A-Za-z0-9\s\@\*\_\.\-]+$/)) {
                alert("No special characters except ('*', '.','-', '_', '@') and spaces are allowed and common name should not start with spaces.");
                g_form.clearValue('subject_alternative_name');
            }
        }
        else if (CA === 'Ejbca') {
            if (!subject_alternative_name.match(/^(\s*,?\s*[0-9a-zA-Z]([-.\w]*[0-9a-zA-Z])*@([0-9a-zA-Z][-\w]*[0-9a-zA-Z]\.)+[a-zA-Z]{2,9})+\s*$/)) {
                alert("Invalid email address.");
                g_form.clearValue('subject_alternative_name');
            }
        }
        else if (CA === 'Entrust') {
            if (!subject_alternative_name.match(/^(\s*,?\s*[0-9a-zA-Z]([-.\w]*[0-9a-zA-Z])*@([0-9a-zA-Z][-\w]*[0-9a-zA-Z]\.)+[a-zA-Z]{2,9})+\s*$/)) {
                alert("Invalid email address.");
                g_form.clearValue('subject_alternative_name');
            }
        } else {
            if (!subject_alternative_name.match(/^([\w+-.%]+@[\w.-]+\.[A-Za-z]{2,4})((,){1}[\w+-.%]+@[\w.-]+\.[A-Za-z]{2,4})*$/)) {
                alert("For Client Certificates the subject alternative name should be a valid EMAIL ADDRESS");
                g_form.clearValue('subject_alternative_name');
            }
        }
    }

}
