catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }
    var privateKeyInDevice = g_form.getValue('private_key_in_device');
    var vendorName = g_form.getValue('vendor_name');
    var certFormat = g_form.getValue('push_certificate_format');
	g_form.clearValue('private_key_location');
    g_form.setMandatory('private_key_location', false);
    g_form.setDisplay('private_key_location', false);
    if (
        (certFormat === 'PKCS#12(*.p12)' || certFormat === 'PKCS#12(*.pfx)') &&
        vendorName === 'LinuxServer' &&
        privateKeyInDevice === 'true'
    ) {
        g_form.clearValue('private_key_location');
        g_form.setMandatory('private_key_location', true);
        g_form.setDisplay('private_key_location', true);
        return;
    }
    if (privateKeyInDevice === 'true') {
        g_form.setValue('customize_push_location', true, true);
        g_form.setReadOnly('customize_push_location', true);
    } else {
        g_form.setReadOnly('customize_push_location', false);
    }
    g_form.setValue('push_root_and_intermediate_certificates', false, false);
    g_form.clearValue('intermediate_and_ca_certificate_directory');
    g_form.setMandatory('intermediate_and_ca_certificate_directory', false);
    g_form.setDisplay('intermediate_and_ca_certificate_directory', false);
}
