catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var common_name = g_form.getValue('common_name');
    var dns = g_form.getValue('dns');
    var ip_address = g_form.getValue('ip_address');
    var san_email = g_form.getValue('san_email');
    if (!san_email.match(/^([\w+-.%]+@[\w.-]+\.[A-Za-z]{2,4})((,){1}[\w+-.%]+@[\w.-]+\.[A-Za-z]{2,4})*$/)) {
        alert("Email should not contain any spaces");
        g_form.clearValue('san_email');
    }
    san_email = g_form.getValue('san_email');
    var san = '';
    if (common_name !== '') {
        san = common_name;
    }
    if (dns !== '') {
        if (san !== '') {
            san = san + ",";
        }
        san = san + dns;
    }
    if (ip_address !== '') {
        if (san !== '') {
            san = san + ",";
        }
        san = san + ip_address;
    }
    if (san_email !== '') {
        if (san !== '') {
            san = san + ",";
        }
        san = san + san_email;
    }
    g_form.setValue('subject_alternative_name', san, san);

}
