catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var common_name = g_form.getValue('common_name');
    var CA = g_form.getValue('certificate_authority');
    if (g_form.getValue('certificate_category').toLowerCase() === 'server' || CA === 'Microsoft Enterprise' || CA === 'HydrantID' || CA === 'DigiCert MPKI') {
        if (CA === 'DigiCert' || CA === 'GlobalSign' || CA === 'GlobalSignMSSL') {
            if (!common_name.match(/^(\*\.)?[A-Za-z0-9._-]+$/)) {
                alert("No special characters except ('*','.','-', '_') are allowed. Common name can start with (*.)");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'Microsoft Enterprise') {
            if (!common_name.match(/^[A-Za-z0-9*._-]*$/) && !common_name.match(/^[^-\s][A-Za-z0-9\s\@\*\_\.\-]+$/)) {
                alert("No special characters except ('*', '.','-', '_', '@') and spaces are allowed and common name should not start with spaces.");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'Entrust') {
            if (!common_name.match(/^[A-Za-z0-9.\-*]*$/)) {
                alert("No special characters except ('.','-','*') are allowed.");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'Ejbca') {
            if (!common_name.match(/^[a-zA-Z_0-9@#\^&*()+=\-[\]\',\.\/\{\}\|\":<>]+$/)) {
                alert("No special characters except @, #, ^, &, _, {, }, [, ], |, /, :, <, >, and :");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'GoDaddy' || CA === 'Trustwave') {
            if (!common_name.match(/^[A-Za-z0-9*.-]*$/)) {
                alert("No special characters except ('*', '.','-') are allowed.");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'AppViewX' || CA === 'AppViewX PKIaaS') {
            if (!common_name.match(/^[A-Za-z0-9*._-]*$/)) {
                alert("No spaces allowed.No special characters except ('*', '.','-', '_') are allowed.");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'Amazon') {
            if (!common_name.match(/^(\*\.)?(((?!-)[A-Za-z0-9-]{0,62}[A-Za-z0-9])\.)+((?!-)[A-Za-z0-9-]{1,62}[A-Za-z0-9])$/)) {
                alert("Common name must contain a domain name and can begin with (*.). No other special characters except ('.','-') are supported.");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'HydrantID') {
            if (!common_name.match(/^[A-Za-z0-9*'=()+,-.\/:?\s]*$/)) {
                alert("Alert : No special characters except ', =, (, ), +, -, ., /, :, ?, comma(,) and Spaces are allowed.");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'DigiCert MPKI') {
            if (!common_name.match(/^([a-zA-Z0-9@*'=()+,-.\/:?_]+\s)*[a-zA-Z0-9@*'=()+,-.\/:?_]+$/)) {
                alert("Alert : No special characters except @, *, ', =, (, ), +, -, ., /, :, ?, _, comma(,) and Spaces are allowed. Consecutive spaces are not allowed");
                g_form.clearValue('common_name');
            }
        }
        common_name = g_form.getValue('common_name');
        var dns = g_form.getValue('dns');
        var ip_address = g_form.getValue('ip_address');
		var san_email = g_form.getValue('san_email');
        if (dns !== '') {
            common_name = common_name + "," + dns;
        }
        if (ip_address !== '') {
            common_name = common_name + "," + ip_address;
        }
        if (san_email !== '') {
            common_name = common_name + "," + san_email;
        }
        g_form.setValue('subject_alternative_name', common_name, common_name);
    } else if (g_form.getValue('certificate_category').toLowerCase() === 'client') {
        if (CA === 'Comodo Certificate Manager') {
            if (!common_name.match(/^[A-Za-z0-9_.-]*$/)) {
                alert("No special characters except ('.','-', '_') are allowed. The common name should not start with a space.");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'Microsoft Enterprise') {
            if (!common_name.match(/^[A-Za-z0-9*._-]*$/) && !common_name.match(/^[^-\s][A-Za-z0-9\s\@\*\_\.\-]+$/)) {
                alert("No special characters except ('*', '.','-', '_', '@') and spaces are allowed and common name should not start with spaces.");
                g_form.clearValue('common_name');
            }
        } else {
            if (!common_name.match(/^[^-\s][A-Za-z0-9\s\@\*\_\.\-]+$/)) {
                alert("No special characters except ('*', '.','-', '_', '@') and spaces are allowed and common name should not start with spaces.");
                g_form.clearValue('common_name');
            }
        }
    } else if (g_form.getValue('certificate_category').toLowerCase() === 'codesigning') {
        if (CA === 'AppViewX') {
            if (!common_name.match(/^[^-\s][A-Za-z0-9\s\@\*\_\.\-]+$/)) {
                alert("No special characters except ('*', '.','-', '_', '@') and spaces are allowed and common name should not start with spaces.");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'DigiCert') {
            if (!common_name.match(/^(\*\.)?[A-Za-z0-9.-_]+$/)) {
                alert("No special characters except ('*','.','-', '_') are allowed. Common name can start with (*.)");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'Ejbca') {
            if (!common_name.match(/^[a-zA-Z_0-9@#\^&*()+=\-[\]\',\.\/\{\}\|\":<>]+$/)) {
                alert("No special characters except @, #, ^, &, _, {, }, [, ], |, /, :, <, >, and :");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'GoDaddy') {
            if (!common_name.match(/^[A-Za-z0-9*.-]*$/)) {
                alert("No special characters except ('*', '.','-') are allowed.");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'Microsoft Enterprise') {
            if (!common_name.match(/^[A-Za-z0-9*._-]*$/) && !common_name.match(/^[^-\s][A-Za-z0-9\s\@\*\_\.\-]+$/)) {
                alert("No special characters except ('*', '.','-', '_', '@') and spaces are allowed and common name should not start with spaces.");
                g_form.clearValue('common_name');
            }
        } else if (CA === 'Trustwave') {
            if (!common_name.match(/^[^-\s][A-Za-z0-9\s\@\*\_\.\-]+$/)) {
                alert("No special characters except ('*', '.','-', '_', '@') and space are allowed. Consecutive spaces are not allowed. Common name should not start with a space.");
                g_form.clearValue('common_name');
            }
		}
    }

}
