catalog_script_client.script:
function onLoad() {
    var ga = new GlideAjax('get_certificate_authority_name');
    ga.addParam("sysparm_name", "getCertificateAuthority");
    ga.getXML(getResponse);
}

function getResponse(response) {
    try {  
        var res = response.responseXML.documentElement.getAttribute('answer');
        var ca_tags = [];
		var iterator;
        try {
            res = JSON.parse(res);
        } catch (err) {
            throw "Couldn't parse ca tag name response from CLM table.";
        }
        g_form.setValue('status', 'New', '');
        g_form.clearOptions('ca_tag_name');
        g_form.addOption('ca_tag_name', "", "Select");
        for (iterator = 0; iterator < res.length; iterator++) {
			var tag_applicable_for = JSON.stringify(res[iterator]['tag_applicable_for']);
			if (tag_applicable_for.includes('Enroll')) {
				ca_tags.push(res[iterator]['ca_tag_name']);
			}
        }
        ca_tags = ca_tags.sort();
		if (ca_tags.length == 0) {
			throw "There is no applicable ca-tags.";
		}
        for (iterator = 0; iterator < ca_tags.length; iterator++) {
            g_form.addOption('ca_tag_name', ca_tags[iterator], ca_tags[iterator]);
        }
    } catch (error) {
        g_form.addErrorMessage("Error: " + error);
        var message = 'Error occured while setting ca tag name in the form - ' + error;
        var ga = new GlideAjax('avx_logger');
        ga.addParam("sysparm_name", "avxLogger");
        ga.addParam("sysparm_level", "Error");
        ga.addParam("sysparm_script", "set_certificate_authority");
        ga.addParam("sysparm_application", "AppViewX");
        ga.addParam("sysparm_message", message);
        ga.getXML(function() {});
	}
}
