catalog_ui_policy.script_true:
function changeFieldLabel(oldLabel, newLabel) {
    $j("span").filter(function() {
        return ($j(this).text() === oldLabel);
    }).html(newLabel);
}

function onCondition() {
	var cmdb_sync = g_form.getValue('cmdb_sync');
    g_form.setReadOnly('status', true);
    g_form.setDisplay('status', true);
    g_form.setDisplay('appviewx_approval', false);
    g_form.setDisplay('appviewx_two_level_approval_email_id', false);
    g_form.setDisplay('appviewx_one_level_approval_email_id', false);
    g_form.setDisplay('certificate_category', false);
    g_form.setDisplay('cmdb_sync', false);
	g_form.setDisplay('query_value', false);
    if (cmdb_sync == 'true') {
        g_form.setDisplay('certificate_reference', true);
        g_form.setMandatory('certificate_reference', true);
        g_form.setMandatory('certificates', false);
        g_form.setDisplay('certificates', false);
    } else {
        g_form.setDisplay('certificates', true);
        g_form.setMandatory('certificates', true);
        g_form.setMandatory('certificate_reference', false);
        g_form.setDisplay('certificate_reference', false);
    }
    if (g_form.getValue('ticket_assignment_group_value') != "Allow user to select assignment group") {
        g_form.setMandatory('ticket_assignment_group', false);
        g_form.setDisplay('ticket_assignment_group', false);
    }
	var meta_data_info = {};
	var mandatory_attributes = {};
	if (g_form.getValue('meta_data_info')) {
		meta_data_info = JSON.parse(g_form.getValue('meta_data_info'));
	}
	if (g_form.getValue('mandatory_attributes')) {
		mandatory_attributes = JSON.parse(g_form.getValue('mandatory_attributes'));
	}
    var meta_length = g_form.getValue('cert_attribute_length');
    for (var i = 0; i < 10; i++) {
        if (i < meta_length) {
            changeFieldLabel('Meta Data ' + (i + 1).toString(), meta_data_info[i]);
            if (mandatory_attributes.indexOf(meta_data_info[i]) !== -1) {
                g_form.setMandatory('meta_data_' + (i + 1).toString(), true);
            }
        } else {
            g_form.setDisplay('meta_data_' + (i + 1).toString(), false);
        }
    }
}
