sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    checkCompanyExist();

    function checkCompanyExist() {
        var op = current.operation();
        var company_name = current.company.name;
        var previous_company_name = previous.company.name;
        var company = current.company;
        var gr = new GlideRecordSecure('x_avx_appviewx_aut_connection_settings_table');
        gr.addQuery('company', company);
        gr.query();
        var count = 1;
		if (op == "update") {
			if (company_name == previous_company_name) {
				count = 0;
			}
		}
        while (gr.next()) {
            count = count + 1;
        }
        if (count <= 1) {
            setDefaultDomain();
        } else {
            gs.addErrorMessage("Unable to update/insert the provided details for the Company - " + company_name + ". An entry for the selected company already exists.");
            current.setAbortAction(true);
        }
    }

    function setDefaultDomain() {

        if (!current.company.nil())
            current.sys_domain = current.company.sys_domain;

        if (current.sys_domain == 'global' || current.sys_domain.nil())
            current.sys_domain = global.getDefaultDomain();

    }

})(current, previous);
