sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var loadingDialog = new GlideDialogWindow("dialog_loading", true);
    loadingDialog.setPreference('table', 'loading');
    loadingDialog.setTitle('Loading...');
    loadingDialog.render();

    function getResponse(response) {
        try {
            var answer = response.responseXML.documentElement.getAttribute('answer');
            try {
                answer = JSON.parse(answer);
            } catch (err) {
                throw new Error("Couldn't parse Amazon Certificate Authority details.");
            }
            var amazon_response = answer.response;
            for (var iterator = 0; iterator < amazon_response.length; iterator++) {
                var account_name = amazon_response[iterator].displayName;
                g_form.addOption('ca_settings_name', account_name, account_name);
            }
            loadingDialog.destroy();
        } catch (error) {
            loadingDialog.destroy();
            g_form.addErrorMessage('Error occured while setting the ca account name ' + error);
            var message = 'Error occured while setting the ca account name ' + error;
            var ga = new GlideAjax('avx_logger');
            ga.addParam("sysparm_name", "avxLogger");
            ga.addParam("sysparm_level", "Error");
            ga.addParam("sysparm_script", "clm_settings_set_ca_setting_name");
            ga.addParam("sysparm_application", "AppViewX");
            ga.addParam("sysparm_message", message);
            ga.getXML(function() {});
        }
    }
    try {
        g_form.clearOptions('ca_settings_name');
        g_form.addOption('ca_settings_name', "", "Select");
        var certificate_authority = newValue;
        var ca_response = g_form.getValue('ca_response');
        try {
            var res = JSON.parse(ca_response);
        } catch (err) {
            throw "Could not parse the ca response details.";
        }
        if (certificate_authority === 'Amazon') {
            var ajax = new GlideAjax('get_aws_accounts');
			var company_name = g_form.getValue("company");
            ajax.addParam('sysparm_name', 'getAwsAccounts');
			ajax.addParam("sysparm_company", company_name);
            ajax.getXML(getResponse);

        } else {
            res = res.response.output;
            for (var iterator = 0; iterator < res.length; iterator++) {
                if (certificate_authority == res[iterator]['certificateAuthority']) {
                    g_form.addOption('ca_settings_name', res[iterator]['name'], res[iterator]['name']);
                }
            }
            loadingDialog.destroy();
        }
    } catch (error) {
        loadingDialog.destroy();
        g_form.addErrorMessage('Error occured while setting the ca account name ' + error);
        var message = 'Error occured while setting the ca account name ' + error;
        var ga = new GlideAjax('avx_logger');
        ga.addParam("sysparm_name", "avxLogger");
        ga.addParam("sysparm_level", "Error");
        ga.addParam("sysparm_script", "clm_settings_set_ca_setting_name");
        ga.addParam("sysparm_application", "AppViewX");
        ga.addParam("sysparm_message", message);
        ga.getXML(function() {});
    }

}
