sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
// 	if (g_form.isNewRecord()) {
	var ga = new GlideAjax('check_ca_tag_name_exist'); //this is the script include
	ga.addParam("sysparm_name", "checkCaTagNameExist");
	ga.addParam("sysparm_caTagName", newValue);
	ga.getXML(getResponse);
//     }

    function getResponse(response) {
        try {
            var res = response.responseXML.documentElement.getAttribute('answer');
            try {
                res = JSON.parse(res);
            } catch (err) {
                throw "Could not parse the property response";
            }
            if ('error' in res) {
                g_form.clearValue('ca_tag_name');
                g_form.addErrorMessage(res.error + " " + newValue);
            }
        } catch (error) {
            g_form.addErrorMessage(error);
        }
    }
}
