sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var ga = new GlideAjax('get_assignment_group_email');
    var loadingDialog = new GlideDialogWindow("dialog_loading", true);
    loadingDialog.setPreference('table', 'loading');
    loadingDialog.setTitle('Loading...');
    loadingDialog.render();
    ga.addParam("sysparm_name", "getGroupEmail");
    ga.addParam("sysparm_groupName", newValue);
    ga.getXML(getResponse);

    function getResponse(response) {
        try {
            var res = response.responseXML.documentElement.getAttribute('answer');
            try {
                res = JSON.parse(res);
            } catch (err) {
                throw "Error while parsing the user group response";
            }
            if (!res.email) {
                g_form.addInfoMessage("Email is not configured for your " + res['name'] + " group. ");
            }
        } catch (error) {
            g_form.addErrorMessage("Error: " + error + "Please check with your assignment group Settings.");
			var message = "Error: " + error + "Please check with your assignment group Settings.";
			var ga = new GlideAjax('avx_logger');
			ga.addParam("sysparm_name", "avxLogger");
			ga.addParam("sysparm_level", "Error");
			ga.addParam("sysparm_script", "validity_assignment_group_email_renew");
			ga.addParam("sysparm_application", "AppViewX");
			ga.addParam("sysparm_message", message);
			ga.getXML(function() {});
        } finally {
            loadingDialog.destroy();
        }
    }
}
