sys_script_client.script:
function onLoad() {
    if (g_form.isNewRecord()) {
        var ga = new GlideAjax('get_appviewx_instance_details'); //this is the script include
        ga.addParam("sysparm_name", "getAVXDetails");
        ga.getXML(getResponse);
    }

    function getResponse(response) {
        try {
            var res = response.responseXML.documentElement.getAttribute('answer');
            try {
                res = JSON.parse(res);
            } catch (err) {
                throw res || "Could not parse the property response";
            }
            g_form.setValue("enter_appviewx_instance_url_with_gateway_port", res["url"]);
            g_form.setValue("enter_mid_server_name_for_appviewx_communication", res["mid_server"]);
            g_form.setValue("appviewx_instance_url", res["oauth_url"]);
            g_form.setValue("client_id", res['client_id']);
            g_form.setValue("client_secret", res['client_secret']);
        } catch (error) {
            g_form.addErrorMessage("Error occured while setting the instance details - " + error);
            var message = "Error occured while setting the instance details - " + error;
            var ga = new GlideAjax('avx_logger');
            ga.addParam("sysparm_name", "avxLogger");
            ga.addParam("sysparm_level", "Error");
            ga.addParam("sysparm_script", "load_instance_details_from_property");
            ga.addParam("sysparm_application", "AppViewX");
            ga.addParam("sysparm_message", message);
            ga.getXML(function() {});
        }

    }
}
