sys_script_include.script:
var get_assignment_group_email = Class.create();
get_assignment_group_email.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getGroupEmail: function() {
        try {
            var groupName = this.getParameter("sysparm_groupName");
            var values = {};
            values['gn'] = groupName;
            var gr = new GlideRecordSecure('sys_user_group');
            gr.addQuery('sys_id', groupName);
            gr.query();
            if (!gr.hasNext()) {
                throw "Could not find the given user group - " + caTagName + " in user group table";
            }
            while (gr.next()) {
                values['name'] = gr.getValue('name');
                values['email'] = gr.getValue('email');
            }
            return JSON.stringify(values);
        } catch (error) {
            var log = new GlideRecordSecure('x_avx_appviewx_aut_logs');
            log.initialize();
            log.level = 'Error';
            log.script = 'get_assignment_group_email';
            log.application = 'AppViewX';
            log.message = 'Error occured while fetching the user group email for approval -  ' + error;
            log.insert();
            return error;
        }
    },

    type: 'get_assignment_group_email'
});
