sys_script_include.script:
var update_ritm_comments = Class.create();
update_ritm_comments.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    updateRitm: function(requestData) {
        try {
			var req_obj = new GlideRecordSecure('sc_req_item');
			req_obj.addQuery('number', requestData['number']);
			req_obj.query();
			if (req_obj.hasNext()) {
				var stgRec = new GlideRecordSecure('x_avx_appviewx_aut_update_ritm_comments');
				stgRec.initialize();
				stgRec.u_comments = requestData['comments'];
				stgRec.u_number = requestData['number'];
				stgRec.insert();
				return 1;
			}
			return 0;
        } catch (error) {
            return 0;
        }
    },

    updateTaskState: function(requestData) {
        try {
            var stgRec = new GlideRecordSecure('x_avx_appviewx_aut_update_task_state');
            stgRec.initialize();
            stgRec.u_task_state = requestData['state'];
            stgRec.u_number = requestData['number'];
            stgRec.insert();
            return 1;
        } catch (error) {
            return 0;
        }
    },

    updateRitmTicket: function(requestData) {
        if (requestData['status'] == 'Success') {
            try {
                var gr = new GlideRecordSecure("sc_req_item");
                gr.addQuery("number", requestData['number']);
                gr.query();
                if (gr.next()) {
                    gr.stage = 'Completed';
                    gr.update();
                } else {
					return 0;
				}
                var stgRec = new GlideRecordSecure('x_avx_appviewx_aut_update_ritm_comments');
                stgRec.u_ritm_state = 3;
                stgRec.u_number = requestData['number'];
                stgRec.insert();
                stgRec = new GlideRecordSecure('x_avx_appviewx_aut_update_request_ticket');
                stgRec.u_req_state = 3;
                stgRec.u_req_request_state = "closed_complete";
                stgRec.u_req_number = requestData['req_number'];
                stgRec.insert();
                return 1;
            } catch (error) {
                return 0;
            }
        } else {
            try {
                gr = new GlideRecordSecure("sc_req_item");
                gr.addQuery("number", requestData['number']);
                gr.query();
                if (gr.next()) {
                    gr.stage = 'closed_incomplete';
                    gr.update();
                } else {
					return 0;
				}
                stgRec = new GlideRecordSecure('x_avx_appviewx_aut_update_ritm_comments');
                stgRec.u_ritm_state = 4;
                stgRec.u_number = requestData['number'];
                stgRec.insert();
                stgRec = new GlideRecordSecure('x_avx_appviewx_aut_update_request_ticket');
                stgRec.u_req_state = 4;
                stgRec.u_req_request_state = "closed_incomplete";
                stgRec.u_req_number = requestData['req_number'];
                stgRec.insert();
                return 1;
            } catch (error) {
                return 0;
            }
        }
    },

    type: 'update_ritm_comments'
});
