sys_script_include.script:
var renew_get_certificate_authority_name = Class.create();
renew_get_certificate_authority_name.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getCaDetails: function() {
        try {
            var caTagName = this.getParameter("sysparm_caTagName");
			var company = this.getParameter("sysparm_company");
            var gr = new GlideRecordSecure('x_avx_appviewx_aut_clm_settings');
            gr.addQuery('ca_tag_name', caTagName);
			if(company) {
				if (gs.getProperty("glide.sys.domain.partitioning") == 'true') {
					gr.addQuery('company', company);
				}
			}
            gr.query();
            var values = {};
            if (!gr.hasNext()) {
                throw "Could not find CA Tag with the name - " + caTagName;
            }
            while (gr.next()) {
				values['ca_account'] = gr.getValue('ca_settings_name');
                values['certificate_authority'] = gr.getValue('ca_type');
                values['cert_category'] = gr.getValue('certificate_category');
                values['certificate_retrieval_file_format'] =
                    gr.getDisplayValue('certificate_retrieval_file_format');
                values['is_approval_required_in_appviewx'] = gr.getValue('is_approval_required_in_appviewx');
                values['approval_levels_in_appviewx'] = gr.getValue('approval_levels_in_appviewx');
                values['appviewx_one_level_approval_mail_id_for_create'] = gr.getValue('appviewx_one_level_approval_mail_id_for_create');
                values['appviewx_two_level_approval_mail_id_for_create'] = gr.getValue('appviewx_two_level_approval_mail_id_for_create');
                values['is_approval_required_in_servicenow_enroll'] = gr.getValue('is_approval_required_in_servicenow_enroll');
                values['approval_levels_in_servicenow'] = gr.getValue('approval_levels_in_servicenow');
                values['certificate_retrieval_mode'] = gr.getValue('certificate_retrieval_mode');
                values['use_approval_settings_from_create'] = gr.getValue('use_approval_settings_from_create');
                values['is_approval_needed_in_appviewx_renewal'] =
                    gr.getValue('is_approval_needed_in_appviewx_renewal');
                values['approval_levels_in_appviewx_renewal'] = gr.getValue('approval_levels_in_appviewx_renewal');
                values['appviewx_one_level_approval_mail_id_for_renew'] = gr.getValue('appviewx_one_level_approval_mail_id_for_renew');
                values['appviewx_two_level_approval_mail_id_for_renew'] = gr.getValue('appviewx_two_level_approval_mail_id_for_renew');
                values['is_approval_needed_in_servicenow_renewal'] = gr.getValue('is_approval_needed_in_servicenow_renewal');
                values['approval_levels_in_servicenow_renewal'] = gr.getValue('approval_levels_in_servicenow_renewal');

                values['use_approval_settings_from_create_for_revoke'] = gr.getValue('use_approval_settings_from_create_for_revoke');
                values['is_approval_needed_in_appviewx_revoke'] =
                    gr.getValue('is_approval_needed_in_appviewx_revoke');
                values['approval_levels_in_appviewx_revoke'] =
                    gr.getValue('approval_levels_in_appviewx_revoke');
                values['appviewx_one_level_approval_mail_id_for_revoke'] = gr.getValue('appviewx_one_level_approval_mail_id_for_revoke');
                values['appviewx_two_level_approval_mail_id_for_revoke'] = gr.getValue('appviewx_two_level_approval_mail_id_for_revoke');
                values['is_approval_needed_in_servicenow_revoke'] = gr.getValue('is_approval_needed_in_servicenow_revoke');
                values['approval_levels_in_servicenow_revoke'] =
                    gr.getValue('approval_levels_in_servicenow_revoke');
                values['revoke_notification_mode'] =
                    gr.getValue('revoke_notification_mode');
                values["logged_in_user_email"] = gs.getUser().getEmail();
                values["certificates_fetch"] = gr.getValue('certificates_fetch');
                values["certificates_fetch_revoke"] = gr.getValue('certificates_fetch_revoke');
                values["loggedin_user_roles"] = gs.getUser().getRoles();
				values['push_to_end_device'] = gr.getValue('push_to_end_device');
				values['assignment_group_selection'] = gr.getValue('assignment_group_selection') || '';
				values['send_certificate'] = gr.getValue('send_certificate_to_end_user') || '';
				values['ticket_assignment_group'] = gr.getValue('ticket_assignment_group') || '';
				values['certificate_attributes'] = gr.getValue('meta_data_configured_in_appviewx');
				values['edit_certificate_attributes'] = gr.getValue('edit_certificate_attributes');
                values["company_name"] = gr.getValue('company') || '';
				values["renewal_method"] = gr.getDisplayValue('renewal_method') || '';
				values["renewal_csr_type"] = gr.getDisplayValue('renewal_csr_type') || '';
				var company_name = gr.getValue('company') || '';
				if (gs.getProperty("glide.sys.domain.partitioning") == 'true') {
					var gr_connection = new GlideRecordSecure('x_avx_appviewx_aut_connection_settings_table');
					gr_connection.addQuery('company', company_name);
					gr_connection.query();
					if (!gr_connection.hasNext()) {
						throw "Could not find AppViewX instance url to fetch the details.";
					}
					while (gr_connection.next()) {
						var cmdb_sync = gr_connection.getDisplayValue('enable_appviewx_inventory_sync') || "false";
					}
					values["cmdb_sync"] = cmdb_sync;
				} else {
					values["cmdb_sync"] = gs.getProperty("x_avx_appviewx_aut.CMDB Sync");
				}
            }
            return JSON.stringify(values);
        } catch (error) {
            var log = new GlideRecordSecure('x_avx_appviewx_aut_logs');
            log.initialize();
            log.level = 'Error';
            log.script = 'renew_get_certificate_authority_name';
            log.application = 'AppViewX';
            log.message = 'Error occured while fetching Certificate authorities from CLM settings table  -  ' + error;
            log.insert();
            return error;
        }
    },

    type: 'renew_get_certificate_authority_name'
});
