sys_script_include.script:
var avx_logger = Class.create();
avx_logger.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    fetchAssignmentGroup: function() {
        var sys_id = gs.getUser().getMyGroups();
        return "sys_idIN" + sys_id;
    },
    avxLogger: function() {
        var level = this.getParameter("sysparm_level");
        var script = this.getParameter("sysparm_script");
        var application = this.getParameter("sysparm_application");
        var message = this.getParameter("sysparm_message");
        var log = new GlideRecordSecure('x_avx_appviewx_aut_logs');
        log.initialize();
        log.level = level;
        log.script = script;
        log.application = application;
        log.message = message;
        log.insert();
    },
    type: 'avx_logger'
});
