function getDefaultValueReferenceQualifier() {
    // Get the current user's record
    var currentUser = gs.getUser();

    // Check if the current user is valid
	var companyName = '';
    if (currentUser.isValid()) {
        // Get the company name from the user's record
        companyName = currentUser.company.getDisplayValue();

        // Log or use the company name as needed
        gs.info("Logged-in user's company name: " + companyName);

        // You can also set the company name in a field or perform other actions
    }
	return companyName;
}
