sys_script_include.script:
var check_ca_tag_name_exist = Class.create();
check_ca_tag_name_exist.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    checkCaTagNameExist: function() {
        var caTagName = this.getParameter("sysparm_caTagName");
        gr = new GlideRecordSecure('x_avx_appviewx_aut_clm_settings');
        gr.query();
        if (!gr.hasNext()) {
            return JSON.stringify({
                "info": "The given CA Tag name is a valid one"
            });
        }
        while (gr.next()) {
            if (caTagName === gr.getValue('ca_tag_name')) {
                return JSON.stringify({
                    "error": "The given CA Tag name already exists"
                });
            }
        }
        return JSON.stringify({
            "info": "The given CA Tag name is a valid one"
        });

    },
    type: 'check_ca_tag_name_exist'
});
