sys_script_include.script:
var get_certificate_authority_name = Class.create();
get_certificate_authority_name.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getCertificateAuthority: function() {
        try {
            var gr = new GlideRecordSecure('x_avx_appviewx_aut_clm_settings');
            var response = [];
            gr.query();
            if (!gr.hasNext()) {
                throw "Could not find the certificate authorities in the CLM settings table";
            }
            while (gr.next()) {
                var values = {};
                values["ca"] = gr.getValue('ca_type');
                values["ca_tag_name"] = gr.getValue('ca_tag_name');
				values["tag_applicable_for"] = gr.getDisplayValue('tag_applicable_for');
                response.push(values);
            }
            return JSON.stringify(response);
        } catch (error) {
            var log = new GlideRecordSecure('x_avx_appviewx_aut_logs');
            log.initialize();
            log.level = 'Error';
            log.script = 'get_certificate_authority_name';
            log.application = 'AppViewX';
            log.message = 'Error occured while fetching Certificate authorities from CLM settings table  -  ' + error;
            log.insert();
            return error;
        }
    },
    type: 'get_certificate_authority_name'
});
