sys_script_include.script:
var get_cert_common_name = Class.create();
get_cert_common_name.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getCertCommonName: function() {
        try {
            var ref_id = this.getParameter("sysparm_ref_id");
			var company = this.getParameter("sysparm_company");
            var gr = new GlideRecordSecure('x_avx_appviewx_aut_certificate_table');
			if (gs.getProperty("glide.sys.domain.partitioning") == 'true') {
				gr.addQuery('company', company);
			}
            gr.addQuery('sys_id', ref_id);
            gr.query();
            var values = {};
            while (gr.next()) {
                values['common_name'] = gr.getValue('common_name');
                values['serial_number'] = gr.getValue('serial_number');
                values['certificate_category'] = gr.getValue('certificate_category');
				if (gr.getValue('private_key_available')== 'Yes' ) {
                values['private_key_available'] = "Private Key Managed in AVX"
				} else {
				values['private_key_available'] = "Private Key Not Managed in AVX"
				}
            }
            return JSON.stringify(values);
        } catch (error) {
            var log = new GlideRecordSecure('x_avx_appviewx_aut_logs');
            log.initialize();
            log.level = 'Error';
            log.script = 'get_cert_common_name';
            log.application = 'AppViewX';
            log.message = 'Error occured while fetching certificate details from certificate table -  ' + error;
            log.insert();
            return error;
        }


    },
    type: 'get_cert_common_name'
});
