sys_script_include.script:
var get_cert_attributes = Class.create();
get_cert_attributes.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getCertificateAttributes: function() {
        try {
            var return_value = {};
            var message;
            var company = this.getParameter("sysparm_company");
            if (gs.getProperty("glide.sys.domain.partitioning") == 'true' && !company) {
                throw "Please select the company to proceed further to get the certificate attributes.";
            }
			if (gs.getProperty("glide.sys.domain.partitioning") == 'true') {
				var gr = new GlideRecordSecure('x_avx_appviewx_aut_connection_settings_table');
				gr.addQuery('company', company);
				gr.query();
				if (!gr.hasNext()) {
					throw "Could not find AppViewX instance url to fetch the details.";
				}
				while (gr.next()) {
					var url = gr.getValue('instance_url') || '';
					var mid_server = gr.getValue('mid_server') || '';
					var cmdb_sync = gr.getDisplayValue('enable_appviewx_inventory_sync') || "false";
				}
			} else {
				url = gs.getProperty("x_avx_appviewx_aut.URL");
				mid_server = gs.getProperty("x_avx_appviewx_aut.MID Server Name");
				cmdb_sync = gs.getProperty("x_avx_appviewx_aut.CMDB Sync");
			}
            if (cmdb_sync == "true") {
                var attribute_obj = new GlideRecordSecure('x_avx_appviewx_aut_certificate_attribute');
                attribute_obj.initialize();
                attribute_obj.addQuery('name', 'certificate_attributes');
				if (gs.getProperty("glide.sys.domain.partitioning") == 'true') {
					attribute_obj.addQuery('company', company);
				}
                attribute_obj.query();
                if (!attribute_obj.hasNext()) {
                    throw "Could not find the certificate_attributes key in the attribute table";
                }
                while (attribute_obj.next()) {
                    var table_response = attribute_obj.getValue('certificate_attribute_response');
                }
                return table_response;
            } else {
				var client_id;
                var script_include_call = new appviewx_oauth_token_generation();
                var token_generation = script_include_call.TestConnection(client_id, company);
                if (token_generation.length == 2 && token_generation[0] == 200) {
                    var parse_response = JSON.parse(token_generation[1]);
                    var token = parse_response['response'];
                } else {
                    throw "Error While Generating the token.";
                }
                var r = new sn_ws.RESTMessageV2();
                if (mid_server != "") {
                    r.setMIDServer(mid_server);
                    r.setEccParameter('skip_sensor', 'true');
                }
                r.setHttpMethod("get");
                r.setRequestHeader("token", token);
                r.setRequestHeader('User-Agent', 'snow');
                r.setRequestHeader("Content-Type", "application/json");
                r.setEndpoint(url + "/avxapi/certificate/attribute?&gwsource=web");
                r.setHttpTimeout(60000);
                var response = r.executeAsync();
                var responseBody, responseMessage;
                if (response.haveError()) {
                    responseBody = response.getBody();
                    try {
                        var res = JSON.parse(responseBody);
                        message = res.message;
                    } catch (err) {
                        message = responseBody;
                    }
                    throw message;
                } else {
                    responseBody = response.getBody();
                    responseBody = JSON.parse(responseBody);
                    return JSON.stringify(responseBody);
                }
            }
        } catch (error) {
            var log = new GlideRecordSecure('x_avx_appviewx_aut_logs');
            log.initialize();
            log.level = 'Error';
            log.script = 'get_cert_attributes';
            log.application = 'AppViewX';
            log.message = 'Error occured while getting certificate attributes from AppViewX -  ' + error;
            log.insert();
            return error;
        }
    },
    type: 'get_cert_attributes'
});
