sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var queryParams = request.queryParams;
	var tableName = queryParams.tableName;
	var ritmNumber = queryParams.ritmNumber;

	var record = new GlideRecord(tableName);
	record.addQuery('number', ritmNumber);
	record.query();
	var flag = 0;
	while(record.next()){
		var obj = {};
		obj.ticket_assignment_group = record.getDisplayValue('variables.ticket_assignment_group');
		flag = 1;
		response.setBody(obj);
	}
	if(flag === 0){
		var responseFailureObject = {};
		response.setStatus(400);
		responseFailureObject["number"] = ritmNumber;
		responseFailureObject["response"] = "The requested RITM number is not found in the Requested Item Table.";
		responseFailureObject["status"] = "Failure";
		response.setBody(responseFailureObject);
	}

})(request, response);
