sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var attachment = new GlideSysAttachment();
    var requestBody = request.body;
    var requestData = requestBody.data;
    var number = requestData["number"];
    var fileName = requestData["fileName"];
    var fileContentType = requestData["contentType"];//'application/zip';
    var fileData = requestData["fileData"];


    var rec = new GlideRecord('sc_req_item');
    rec.addQuery('number', number);
    rec.query();
    if (rec.next()) {
        var agr = attachment.writeBase64(rec, fileName, fileContentType, fileData);
		if(agr){
        var responseObj = {};
		responseObj["number"] = number;
		responseObj["response"] = "Certificate attach to the ritm ticket (" + number + ") is successful."; 
        responseObj["status"] = "Success";
        response.setBody(responseObj);
		}else{
			var responseFailureObject = {};
			response.setStatus(400);
			responseFailureObject["number"] = number;
			responseFailureObject["response"] = "Certificate attached to RITM ticket failed. Check the allowed file extensions in servicenow";
			responseFailureObject["status"] = "Failure";
			response.setBody(responseFailureObject);
		}
    }else{
		responseFailureObject = {};
		response.setStatus(400);
		responseFailureObject["number"] = number;
		responseFailureObject["response"] = "The requested RITM number is not found.";
		responseFailureObject["status"] = "Failure";
		response.setBody(responseFailureObject);
	}

})(request, response);
