sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var requestBody = request.body;
	var requestData = requestBody.data;	
	var requestObj;
	if(requestData instanceof Array){
		for(requestObj in requestData){
			new update_ritm_comments().updateRitm(requestData[requestObj]);
		}
	}else{
		var flag = new update_ritm_comments().updateRitm(requestData);
		if(flag === 1){
			var responseObject = {};
			responseObject["response"] = "The comments were updated in the requested RITM ticket.";
			responseObject["status"] = "Success";
			response.setBody(responseObject);
		}
		else{
			var responseFailureObject = {};
			response.setStatus(400);
			responseFailureObject["response"] = "Error occured while updating the comments in the ritm ticket.";
			responseFailureObject["status"] = "Failure";
			response.setBody(responseFailureObject);
		}
	}
})(request, response);
