sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var queryParams = request.queryParams;
	var tableName = queryParams.tableName;
	var ritmNumber = queryParams.ritmNumber;
	var sysId = queryParams.sysId;

	var record = new GlideRecord(tableName);
	record.addQuery('request_item', sysId);
	record.query();
	var flag = 0;
	while(record.next()){
		var obj = {};
		obj.display_value = record.getDisplayValue('number');
		obj.request_item = record.getDisplayValue('request_item');
		if(ritmNumber == obj.request_item){
			flag = 1;
			response.setBody(obj);
		}
	}
	if(flag === 0){
		var responseFailureObject = {};
		response.setStatus(400);
		responseFailureObject["number"] = ritmNumber;
		responseFailureObject["response"] = "The requested RITM number is not found under any of the TASK.";
		responseFailureObject["status"] = "Failure";
		response.setBody(responseFailureObject);
	}

})(request, response);
