sys_variable_value.value:
// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the overall approval status for this approval.
//
// This script is responsible for setting the approval state for each group that is part of this approval activity before
// returning the overall approval state for all of the group(s).
//
// When called, the following variables are available to the script:
//
//    Overall for all of the groups that are part of this approval activity:
//        counts.total = total number of groups that are part of this approval
//        counts.approved = # of groups that approved so far
//        counts.rejected = # of groups that rejected so far
//        counts.requested = # of groups that are pending approval
//        counts.not_requested = # of groups that are not pending approval
//        counts.not_required = # of groups that approval is not required
//
//    And for each group:
//        groups[group_id].total = total number of users that are part of this group's approval
//        groups[group_id].approved = # of users that approved so far
//        groups[group_id].rejected = # of users that rejected so far
//        groups[group_id].requested = # of users that are pending approval
//        groups[group_id].not_requested = # of users that are not pending approval
//        groups[group_id].not_required = # of users that approval is not required
//
//        groups[group_id].approvalIDs[state] = array of user ids that are at the specified approval state
//
//        Note: Iterate the groups using:
//            for (var id in groups) {
//                 var group = groups[id];
//                 ...  group.total ...
//            }

var answer = [];
current.comments = "Your certificate request is pending for approval.";
answer.push(workflow.scratchpad.user_group_name);

// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the overall approval status for this approval.
//
// This script is responsible for setting the approval state for each group that is part of this approval activity before
// returning the overall approval state for all of the group(s).
//
// When called, the following variables are available to the script:
//
//    Overall for all of the groups that are part of this approval activity:
//        counts.total = total number of groups that are part of this approval
//        counts.approved = # of groups that approved so far
//        counts.rejected = # of groups that rejected so far
//        counts.requested = # of groups that are pending approval
//        counts.not_requested = # of groups that are not pending approval
//        counts.not_required = # of groups that approval is not required
//
//    And for each group:
//        groups[group_id].total = total number of users that are part of this group's approval
//        groups[group_id].approved = # of users that approved so far
//        groups[group_id].rejected = # of users that rejected so far
//        groups[group_id].requested = # of users that are pending approval
//        groups[group_id].not_requested = # of users that are not pending approval
//        groups[group_id].not_required = # of users that approval is not required
//
//        groups[group_id].approvalIDs[state] = array of user ids that are at the specified approval state
//
//        Note: Iterate the groups using:
//            for (var id in groups) {
//                 var group = groups[id];
//                 ...  group.total ...
//            }

var answer = [];
current.comments = "Your certificate request is pending for approval.";
answer.push(workflow.scratchpad.user_group_name);

function set_digicert_custom_attribute(profile_name) {
    try {
        var custom_cert_attr = [];
        var custom_attributes = [];
        var digicert_mpki_profile = current.variables.digicert_mpki_profiles;
        for (var iterator = 0; iterator < profile_name.length; iterator++) {
            if (profile_name[iterator].profileName == digicert_mpki_profile) {
                custom_attributes = profile_name[iterator].customAttributes || [];
                break;
            }
        }
        var custom_attributes_length = custom_attributes.length;
        if (custom_attributes_length > 0) {
            for (var meta_data = 1; meta_data < custom_attributes_length + 1; meta_data++) {
				var custom_cert_attributes = {};
				var field_name = custom_attributes[meta_data - 1].name;
				var field_id = "custom_meta_data_" + (meta_data.toString());
                custom_cert_attributes['custom_attribute'] = field_name;
				custom_cert_attributes['custom_attribute_value'] = (current.variables[field_id] || '').toString();
				custom_cert_attr.push(custom_cert_attributes);
            }
			return custom_cert_attr;
        } else {
			return [];
		}
    } catch (error) {
		return [];
    }
}


activity.result = (function() {
    try {
        var cert_attr = [];
        var cert_attributes = {};
		var company = current.variables.company_name;
		var clm_cert_attribute;
		var tag_name = current.variables.ca_tag_name;
        cert_attributes['attribute'] = 'requestor';
		cert_attributes['attribute_value'] = (workflow.scratchpad.requestor_email || '');
		cert_attr.push(cert_attributes);
		cert_attributes = {};
		cert_attributes['attribute'] = 'requestor_assignment_group';
		cert_attributes['attribute_value'] = (workflow.scratchpad.assignment_group_name || '');
		cert_attr.push(cert_attributes);
        gr = new GlideRecordSecure('x_avx_appviewx_aut_clm_settings');
        gr.addQuery('ca_tag_name', tag_name);
		if (gs.getProperty("glide.sys.domain.partitioning") === 'true') {
			gr.addQuery('company', company);
		}
        gr.query();
        while (gr.next()) {
            clm_cert_attribute = gr.getValue('meta_data_configured_in_appviewx');
        }
        var meta_info = JSON.parse(clm_cert_attribute) || {};
        var meta_length = Object.keys(meta_info).length;
        if (meta_length > 0) {
            var obj = [];
            for (var key in meta_info) {
                var key_value = meta_info[key];
                obj.push(key_value);
            }
            for (var i = 1; i < meta_length + 1; i++) {
                cert_attributes = {};
                var field_name = "meta_data_" + (i.toString());
                cert_attributes['attribute'] = obj[i - 1];
				cert_attributes['attribute_value'] = (current.variables[field_name] || '').toString();
				cert_attr.push(cert_attributes);
            }
        }
        workflow.scratchpad.certificate_attributes_update = JSON.stringify(cert_attr);
		var CA = current.variables.certificate_authority;
		var custom_attributes = [];
		if (CA == 'DigiCert MPKI') {
			var digicert_mpki_ca_details = current.variables.digicert_mpki_ca_details;
			try {
				digicert_mpki_ca_details = JSON.parse(digicert_mpki_ca_details);
				digicert_mpki_ca_details = digicert_mpki_ca_details.endEntityProfiles;
				custom_attributes = set_digicert_custom_attribute(digicert_mpki_ca_details);
			} catch (err) {
				return "true";
			}
		}
		workflow.scratchpad.custom_certificate_attributes = JSON.stringify(custom_attributes);
        return "true";
    } catch (error) {
        current.comments = 'Failure while processing the service request.';
		current.stage = "closed_incomplete";
        current.state = 4;
        var reqRecord = current.request.getRefRecord();
        // Set the values on the request table
        reqRecord.state = 4;
        reqRecord.request_state = "closed_incomplete";
        reqRecord.update();
        var log = new GlideRecord('x_avx_appviewx_aut_logs');
        log.initialize();
        log.level = 'Error';
        log.script = 'Certificate attribute generation';
        log.application = 'AppViewX';
        log.message = 'Error while generating the certificate generation ' + error;
        log.insert();
        gs.addErrorMessage('Error while generating the cert attributes');
        return "false";
    }
})();

function set_digicert_custom_attribute(profile_name) {
    try {
        var custom_cert_attr = [];
        var custom_attributes = [];
        var digicert_mpki_profile = current.variables.digicert_mpki_profiles;
        for (var iterator = 0; iterator < profile_name.length; iterator++) {
            if (profile_name[iterator].profileName == digicert_mpki_profile) {
