activity.result = (function() {
    try {
        var reqRecord = current.request.getRefRecord();
        reqRecord.short_description = 'AppViewX Certificate Creation';
        reqRecord.update();
		var file_upload_sys_id = current.variables.upload_csr;
        var sysAttachmentGR = new GlideRecord('sys_attachment');
        var attachmentGSA = new GlideSysAttachment();
        sysAttachmentGR.addQuery('table_name', 'ZZ_YY' + current.getTableName());
        sysAttachmentGR.addQuery('table_sys_id', current.sys_id);
		sysAttachmentGR.addQuery('sys_id', file_upload_sys_id);
        sysAttachmentGR.query();
        while (sysAttachmentGR.next()) {
            workflow.scratchpad.cert_content = attachmentGSA.getContent(sysAttachmentGR);
			workflow.info(workflow.scratchpad.cert_content);
        }
        workflow.scratchpad.sys_id = current.sys_id;
        var ca_tag_name = current.variables.ca_tag_name;
        var gr = new GlideRecord('x_avx_appviewx_aut_clm_settings');
        gr.query('ca_tag_name', ca_tag_name);
        if (!gr.hasNext()) {
            throw "Could not find CA Tag with the name - " + ca_tag_name + " in the CLM Settings table";
        }
        while (gr.next()) {
            workflow.scratchpad.is_approval_needed_in_servicenow = gr.
            is_approval_required_in_servicenow_enroll;
            workflow.scratchpad.approval_levels_in_appviewx = gr.approval_levels_in_appviewx;
            workflow.scratchpad.approval_levels_in_servicenow = gr.approval_levels_in_servicenow;
            workflow.scratchpad.is_approval_needed_in_appviewx = gr.is_approval_needed_in_appviewx;
            workflow.scratchpad.appviewx_one_level_approval_mail_id_for_create = gr.appviewx_one_level_approval_mail_id_for_create;
            workflow.scratchpad.servicenow_approver = gr.approver_selection;
            workflow.scratchpad.cert_group_selection = gr.certificate_group_selection;
            workflow.scratchpad.cert_fetch = gr.certificates_fetch;
            workflow.scratchpad.time_zone = gr.time_zone;
			workflow.scratchpad.second_level_approver_selection = gr.enroll_approver_selection_two;
            workflow.scratchpad.push_to_end_device = gr.push_to_end_device;
            workflow.scratchpad.time_zone = gr.time_zone;
            workflow.scratchpad.create_catalog_task_ticket = gr.create_catalog_task_ticket;
            workflow.scratchpad.task_priority = gr.task_priority || '';
            workflow.scratchpad.task_assigned_to = gr.task_assigned_to || '';
            workflow.scratchpad.task_assignment_group = gr.task_assignment_group || '';
            workflow.scratchpad.send_certificate_to_end_user = gr.send_certificate_to_end_user;
            var ticket_assignment_group = gr.ticket_assignment_group || '';
            var ticket_assignment_group_name = gr.ticket_assignment_group_name || '';
            if (gr.approver_selection == 'Assignment Group') {
                workflow.scratchpad.user_group_name = gr.assignment_group;
            }
            if (gr.enroll_approver_selection_two == 'Assignment Group') {
                workflow.scratchpad.second_level_approval_group = gr.enroll_user_group_approval_two;
            }
            workflow.scratchpad.assignment_group_selection = gr.assignment_group_selection;
            if (workflow.scratchpad.assignment_group_selection == 'Use assignment group from clm settings') {
                workflow.scratchpad.push_assignment_group = gr.getDisplayValue('push_user_group');
            }
            workflow.scratchpad.create_workflow_name = gr.workflow_name_for_create_appviewx;
            workflow.scratchpad.appviewx_two_level_approval_mail_id_for_create = gr.appviewx_two_level_approval_mail_id_for_create;
            workflow.scratchpad.cert_format = gr.certificate_retrieval_file_format;
            workflow.scratchpad.certificate_retrieval_mode = gr.certificate_retrieval_mode;
			workflow.scratchpad.zip_certificate_file = gr.zip_the_certificate_file || false;
			workflow.scratchpad.trust_cert_file = gr.attach_trust_cert || false;
        }
        var company = current.variables.company_name;
        if (gs.getProperty("glide.sys.domain.partitioning") === 'true') {
            gr = new GlideRecordSecure('x_avx_appviewx_aut_connection_settings_table');
            gr.addQuery('company', company);
            gr.query();
            if (!gr.hasNext()) {
                throw "Could not find AppViewX instance url to fetch the details.";
            }
            while (gr.next()) {
                workflow.scratchpad.cmdb_sync = gr.getDisplayValue('enable_appviewx_inventory_sync') || 'false';
            }
        } else {
            workflow.scratchpad.cmdb_sync = gs.getProperty("x_avx_appviewx_aut.CMDB Sync");
        }
		var gr_sys_user = new GlideRecord('sys_user');
		gr_sys_user.get(gs.getUserID());
		workflow.scratchpad.manager_id = gr_sys_user.manager;
        // if (workflow.scratchpad.servicenow_approver == 'Manager') {
        //     gr_sys_user = new GlideRecord('sys_user');
        //     gr_sys_user.get(gs.getUserID());
        //     workflow.scratchpad.manager_id = gr_sys_user.manager.user_name;
        //     var manager_email = gr_sys_user.manager.email;
        //     workflow.scratchpad.servicenow_approver_mail = manager_email;
        // } else if (workflow.scratchpad.servicenow_approver == 'Assignment Group') {
        //     var user_group_mail;
        //     var gr_user_group = new GlideRecord('sys_user_group');
        //     gr_user_group.addQuery('sys_id', workflow.scratchpad.user_group_name);
        //     gr_user_group.query();
        //     if (!gr_user_group.hasNext()) {
        //         throw "Could not find the given user group in the user table";
        //     }
        //     while (gr_user_group.next()) {
        //         user_group_mail = gr_user_group.getValue('email');
        //     }
        //     workflow.scratchpad.servicenow_approver_mail = user_group_mail;
        // }
        var myGroups = gs.getUser().getMyGroups().toString().split(",");
        var attribute_mail = "";
        var assignment_group_name = "";
        for (var iterator = 0; iterator < myGroups.length; iterator++) {
            if (myGroups[iterator]) {
                var group = new GlideRecord('sys_user_group');
                group.addQuery('sys_id', myGroups[iterator]);
                group.query();
                // if (!group.hasNext()) {
                //     throw "Could not find the given user group in the user table";
                // }
                while (group.next()) {
                    if (group.getValue('email')) {
                        if (attribute_mail) {
                            attribute_mail = attribute_mail + "," + group.getValue('email');
                        } else {
                            attribute_mail = group.getValue('email');
                        }
                    }
                    if (assignment_group_name) {
                        assignment_group_name = assignment_group_name + "," + group.getValue('name');
                    } else {
                        assignment_group_name = group.getValue('name');
                    }
                }
            }
        }
        if (ticket_assignment_group == "Allow user to select assignment group") {
            workflow.scratchpad.ticket_assignment_group_value = current.variables.ticket_assignment_group;
        } else if (ticket_assignment_group == "Use assignment group from clm settings") {
            workflow.scratchpad.ticket_assignment_group_value = ticket_assignment_group_name;
        } else if (ticket_assignment_group == "Use logged-in user assignment group") {
            workflow.scratchpad.ticket_assignment_group_value = myGroups[0];
        }
        workflow.info(workflow.scratchpad.ticket_assignment_group_value);
        if (ticket_assignment_group != "None of the group") {
            current.assignment_group = workflow.scratchpad.ticket_assignment_group_value;
        }
        workflow.scratchpad.ticket_assignment_group = ticket_assignment_group;
        if (workflow.scratchpad.cert_fetch == "Based on User Group In ServiceNow") {
            workflow.scratchpad.attribute_mail = assignment_group_name;
        }
        workflow.scratchpad.assignment_group_name = assignment_group_name;
        var push_to_end_device = current.variables.push_to_end_device;
        if (push_to_end_device == "true") {
            if (workflow.scratchpad.assignment_group_selection == 'Use logged-in user assignment group') {
                workflow.scratchpad.push_assignment_group = workflow.scratchpad.assignment_group_name.split(",")[0];
            } else if (workflow.scratchpad.assignment_group_selection == 'Allow user to enter assignment group') {
                var push_assignment_group = current.variables.push_assignment_group;
                group = new GlideRecord('sys_user_group');
                group.addQuery('sys_id', push_assignment_group);
                group.query();
                if (!group.hasNext()) {
                    throw "Could not find the given user group in the user table";
                }
                while (group.next()) {
                    workflow.scratchpad.push_assignment_group = group.getValue('name');
                }
            }
        }
        workflow.scratchpad.requestor_email = gs.getUser().getEmail();
        workflow.info(gs.getUserName());
        workflow.scratchpad.user_name = gs.getUserName();
        if (current.variables.push_to_end_device == 'true') {
            if (workflow.scratchpad.cmdb_sync == 'true') {
                var profiles = current.variables.profile_name_reference;
                var profile_obj = new GlideRecord('x_avx_appviewx_aut_vendor_profiles_table');
                profile_obj.addQuery('sys_id', 'IN', profiles);
                profile_obj.query();
                var profile_names = "";
                while (profile_obj.next()) {
                    if (profile_names) {
                        profile_names = profile_names + "," + profile_obj.getValue('profile_name');
                    } else {
                        profile_names = profile_obj.getValue('profile_name');
                    }
                }
                workflow.scratchpad.profile_name = profile_names;
            } else {
                workflow.scratchpad.profile_name = current.variables.profile_name;
            }
        }
        return "true";
    } catch (e) {
        var log = new GlideRecord('x_avx_appviewx_aut_logs');
        log.initialize();
        log.level = 'Error';
        log.script = 'Setting scratchpad values.';
        log.application = 'AppViewX';
        log.message = 'Error occurred while generating the scratchpad values ' + e;
        log.insert();
        current.stage = "closed_incomplete";
        current.state = 4;
        current.comments = "Failure while processing the service request.";
        reqRecord = current.request.getRefRecord();
        // Set the values on the request table
        reqRecord.state = 4;
        reqRecord.request_state = 'closed_incomplete';
        reqRecord.update();
        workflow.error("Error While Processing the Workflow " + e);
        return "false";
    }

})();

