var scTask = new GlideRecord('sc_task');
scTask.addQuery('parent', current.sys_id);
scTask.query();

while(scTask.next()){
	var state_value = scTask.state.getDisplayValue();
}
if (state_value === 'Closed Complete') {
    scTask = new GlideRecord('sc_task');
    scTask.addQuery('parent', current.sys_id);
    scTask.query();
    while (scTask.next()) {
        scTask.work_notes = "Certificate creation has been successfully completed.";
        scTask.update();
    }
    current.state = 3;
    current.stage = "complete";
} else {
    scTask = new GlideRecord('sc_task');
    scTask.addQuery('parent', current.sys_id);
    scTask.query();
    while (scTask.next()) {
        scTask.work_notes = "Certificate creation is failed in AppViewX.";
        scTask.update();
    }
    current.state = 4;
    current.stage = "closed_incomplete";
}

