}

activity.result = (function() {
    try {
        var log = new GlideRecord('x_avx_appviewx_aut_logs');
        log.initialize();
        if (workflow.scratchpad.is_approval_needed_in_servicenow === "true") {
            current.comments = "Your certificate request has been approved.";
        }
		var client_id;
		var company = current.variables.company_name;
		workflow.info(gs.getUserName());
		if (gs.getProperty("glide.sys.domain.partitioning") === 'true') {
			var gr = new GlideRecordSecure('x_avx_appviewx_aut_connection_settings_table');
			gr.addQuery('company', company);
			gr.query();
			if (!gr.hasNext()) {
				throw "Could not find AppViewX instance url to fetch the details.";
			}
			while (gr.next()) {
				var url = gr.getValue('instance_url') || '';
				var mid_server = gr.getValue('mid_server') || '';
			}
		} else {
			url = gs.getProperty("x_avx_appviewx_aut.URL");
			mid_server = gs.getProperty("x_avx_appviewx_aut.MID Server Name");
		}
        var r = new sn_ws.RESTMessageV2();
        var script_include_call = new appviewx_oauth_token_generation();
        var token_generation = script_include_call.TestConnection(client_id, company);
        if (token_generation.length == 2 && token_generation[0] == 200) {
            var parse_response = JSON.parse(token_generation[1]);
            var token = parse_response['response'];
        } else {
            throw "Error while generating the Token.";
        }
        var form = current.variables;
        var upload_cert_content;
        if (form.csr_input_method == 'Upload CSR') {
            if (form.csr_upload_option == 'Paste CSR') {
                upload_cert_content = (form.paste_csr).toString();
            } else {
                upload_cert_content = (workflow.scratchpad.cert_content).toString();
            }
        }
        var san_values = [];
        workflow.scratchpad.add_dns_values = (form.add_dns_values).toString();
        workflow.scratchpad.add_ip_address = (form.add_ip_address).toString();
		workflow.scratchpad.add_email_address = (form.add_email_address).toString();
        if (workflow.scratchpad.add_dns_values === "true") {
            san_values.push("DNS");
        }
        if (workflow.scratchpad.add_ip_address === "true") {
            san_values.push("IP Address");
        }
        var cert_type;
        if (form.certificate_authority == 'DigiCert') {
            cert_type = (form.certificate_type).toString();
        } else if (form.certificate_authority == 'Entrust') {
            cert_type = (form.entrust_certificate_type).toString();
        } else if (form.certificate_authority == 'GoDaddy') {
            cert_type = (form.godaddy_certificate_type).toString();
        } else if (form.certificate_authority == 'Comodo Certificate Manager') {
            cert_type = (form.sectigo_certificate_type).toString();
        } else if (form.certificate_authority == 'GlobalSign') {
			cert_type = (form.globalsign_certificate_type).toString();
		} else if (form.certificate_authority == 'GlobalSignMSSL') {
			cert_type = (form.globalsign_mssl_certificate_type).toString();
		} else if (form.certificate_authority == 'Trustwave') {
			cert_type = (form.trustwave_certificate_type).toString();
		} else if (form.certificate_authority == 'HydrantID') {
			cert_type = (form.hydrantid_policy_name).toString();
		} else if (form.certificate_authority == 'SwissSign') {
			cert_type = (form.swisssign_product).toString();
		}
        var client_san;
        if (form.certificate_category == 'Client' && form.certificate_authority != 'Microsoft Enterprise') {
            san_values.push("Email Address");
            client_san = form.subject_alternative_name || '';
        } else if (form.certificate_authority == 'Microsoft Enterprise') {
			if (workflow.scratchpad.add_email_address === "true") {
				san_values.push("Email Address");
				client_san = form.san_email || '';
			}
        }
        log.level = 'Info';
        log.script = 'create_certificate';
        log.application = 'AppViewX';
        log.message = 'Triggering new certificate request API';
        log.insert();
        var payload = {
            "payload": {
                "header": {
                    "workflowName": (workflow.scratchpad.create_workflow_name || '').toString()
                },
                "data": {
                    "input": {
                        "requestData": [{
                            "sequenceNo": 1,
                            "scenario": "scenario",
                            "fieldInfo": {
                                "cert_category": (form.certificate_category || '').toString(),
                                "certificate_policy": (form.certificate_policy || '').toString(),
                                "certificate_authority": (form.certificate_authority).toString(),
                                "input_method": (form.csr_input_method || '').toString(),
                                "ca_account": (form.ca_account || '').toString(),
                                "division": (form.division || '').toString(),
                                "cert_type": (cert_type || '').toString(),
                                "auto_renew": "Off",
                                "renew_before": "",
                                "auto_regenerate": "Off",
                                "regenerate_before": "",
                                "upload_csr": (upload_cert_content || '').toString(),
                                "common_name": ((form.common_name || '').toString()).trim(),
                                "san": (san_values.join(",") || '').toString(), //"DNS,IP Address",
                                "directory_names": "",
                                "dns_names": (form.dns || '').toString(),
                                "rfc822names": (client_san || '').toString(),
                                "ip_addresses": (form.ip_address || '').toString(),
                                "registered_ids": "",
                                "uris": "",
                                "othernames": "",
                                "organization": (form.organization || '').toString(),
                                "organization_unit": (form.organization_unit || '').toString(),
                                "locality": (form.locality || '').toString(),
                                "state": (form.state || '').toString(),
                                "country": (form.country || '').toString(),
                                "email_address": (form.email_address || '').toString(),
                                "digicert_postal_code": "",
                                "validity_unit": (form.validity_unit || '').toString(),
                                "validity_value": (form.validity_value || '').toString(),
                                "challenge_password": "",
                                "hash_function": (form.hash_function || '').toString(),
                                "key_type": (form.key_type || '').toString(),
                                "bit_length": (form.bit_length || '').toString(),
                                "elliptic_curve": (form.ecdsa_curve || '').toString(),
                                "digicert_server_type": (form.server_type || '').toString(),
                                "ejbca_end_entity_profile_name": (form.ejbca_end_entity_profile_name || '').toString(),
                                "ejbca_end_entity_user_name": (form.ejbca_end_entity_user_name || '').toString(),
                                "ejbca_issuer_common_name": (form.ejbca_issuer_common_name || '').toString(),
                                "ejbca_certificate_profile_name": (form.ejbca_certificate_profile_name || '').toString(),
                                "entrust_additional_emails": "",
                                "microsoft_enterprise_template_name": (form.microsoft_template || '').toString(),
                                "user_email": (form.email_id || '').toString(),
                                "sync_mode": "Sync",
                                "ttl": "300",
                                "appviewx_approval": (workflow.scratchpad.approval_levels_in_appviewx || '').toString(),
                                "cert_format": ((form.certificate_issuance_format || '').toString()).trim(),
                                "cert_issue_mode": (workflow.scratchpad.certificate_retrieval_mode || '').toString(),
                                "requestor_id": (workflow.scratchpad.requestor_email || '').toString(),
                                "sys_id": (workflow.scratchpad.sys_id || '').toString(),
                                "one_level_approver_mail_id": (workflow.scratchpad.appviewx_one_level_approval_mail_id_for_create || '').toString(),
                                "two_level_approver_mail_id": (workflow.scratchpad.appviewx_two_level_approval_mail_id_for_create || '').toString(),
                                "ritm_number": (current.number || '').toString(),
                                "req_number": (current.request.getDisplayValue() || '').toString(),
                                "req_sys_id": (current.request || '').toString(),
                                "cert_attributes": (JSON.parse(workflow.scratchpad.certificate_attributes_update) || []),
                                "appviewx_url": url.toString(),
                                "certificate_group_selection": (workflow.scratchpad.cert_group_selection || '').toString(),
                                "servicenow_user_name": (workflow.scratchpad.user_name || '').toString(),
                                "amazonCertValidationType": (form.certificate_validation_type || '').toString(),
                                "CertificateTransparencyLoggingPreference": (form.certificate_transparency_logging_preference || '').toString(),
                                "godaddy_slot_size": (form.godaddy_slot_size || '').toString(),
                                "issuer": (form.issuer || '').toString(),
                                "region": (form.region || '').toString(),
                                "ca_signing_algorithm": (form.ca_signing_algorithm || '').toString(),
                                "sectigo_server_type": (form.sectigo_server_type || '').toString(),
                                "assignment_group_name": (workflow.scratchpad.assignment_group_name || '').toString(),
                                "cmdb_sync": (workflow.scratchpad.cmdb_sync || '').toString(),
                                "push_to_end_device": (form.push_to_end_device || '').toString(),
                                "push_assignment_group": (workflow.scratchpad.push_assignment_group || '').toString(),
                                "send_certificate": (workflow.scratchpad.send_certificate_to_end_user || '').toString(),
                                "vendor_name": (form.vendor_name || '').toString(),
                                "device_name": (form.device_name || '').toString(),
                                "profile_name": (workflow.scratchpad.profile_name || '').toString(),
								"time_zone": (workflow.scratchpad.time_zone || '').toString(),
								"push_root_and_intermediate_certificates": (form.push_root_and_intermediate_certificates || '').toString(),
								"push_certificate_format": (form.push_certificate_format || '').toString(),
								"customize_push_location": (form.customize_push_location || '').toString(),
								"private_key_in_device": (form.private_key_in_device || '').toString(),
								"intermediate_and_ca_certificate_directory": (form.intermediate_and_ca_certificate_directory || '').toString(),
								"certificate_directory": (form.certificate_directory || '').toString(),
								"key_directory": (form.key_directory || '').toString(),
								"keystore_location": (form.keystore_location || '').toString(),
								"keystore_password": (form.keystore_password || '').toString(),
								"service_restart": (form.service_restart || '').toString(),
								"private_key_location": (form.private_key_location || '').toString(),
								"catalog_task": (workflow.scratchpad.create_catalog_task_ticket || '').toString(),
								"domain_admin_email": (form.domain_admin_email || '').toString(),
								"business_category": (form.business_category || '').toString(),
								"incorporating_agency_reg_no": (form.incorporating_agency_reg_no || '').toString(),
								"designation": (form.designation || '').toString(),
								"globalsign_mssl_profile_name": (form.globalsign_mssl_profile_name || '').toString(),
								"avx_issuer_location": (form.avx_issuer_location || '').toString(),
								"avx_issuer_name": (form.avx_issuer_name || '').toString(),
								"organization_address": (form.organization_address || '').toString(),
								"csr_zip_code": (form.csr_zip_code || '').toString(),
								"form_assignment_group": (form.ticket_assignment_group.name || '').toString(),
								"zip_certificate_file": (workflow.scratchpad.zip_certificate_file || false).toString(),
								"digicert_mpki_profile_name": (form.digicert_mpki_profiles || '').toString(),
								"custom_attributes": (JSON.parse(workflow.scratchpad.custom_certificate_attributes) || []),
								"zip_trust_certificate": (workflow.scratchpad.trust_cert_file || false).toString(),
								"division_id": (form.division_id || '').toString(),
								"swisssign_client_name": (form.swisssign_client_name || '').toString()
                            }
                        }]
                    },
                    "globalData": {},
                    "task_action": 1
                }
            }
        };
		workflow.info(JSON.stringify(payload));
        if (mid_server != "") {
            r.setMIDServer(mid_server);
            r.setEccParameter('skip_sensor', 'true');
        }
        r.setHttpMethod("post");
        r.setRequestHeader("token", token);
        r.setRequestHeader('User-Agent', 'snow');
        r.setRequestHeader("Content-Type", "application/json");
        r.setEndpoint(url + "/avxapi/visualworkflow-submit-request?gwsource=web");
        r.setRequestBody(JSON.stringify(payload));
        r.setHttpTimeout(30000);
        var response = r.executeAsync();
        var reqRecord = current.request.getRefRecord();
        if (response.haveError()) {
            var responseMessage = response.getErrorMessage() || '';
            var responseBody = response.getBody() || '';
            log.level = 'Error';
            log.script = 'new_certificate_request';
            log.application = 'AppViewX';
            log.message = 'Certificate request failed ' + responseBody + '\nMessage: ' + responseMessage;
            log.insert();
            responseBody = JSON.parse(responseBody);
            form.request_id = "Failed to submit certificate request";
            form.appviewx_response = responseBody["message"];
            current.stage = "closed_incomplete";
            current.state = 4;
            // Set the values on the request table
            reqRecord.state = 4;
            reqRecord.request_state = "closed_incomplete";
            reqRecord.update();
			current.comments = "Failure while processing the service request.\n" + responseBody["message"];
            return "false";
        } else {
            responseBody = response.getBody();
            responseBody = JSON.parse(responseBody);
            var avx_id = responseBody["response"]["requestId"];
            log.level = 'Info';
            log.script = 'new_certificate_request';
            log.application = 'AppViewX';
            log.message = 'Successfully triggered appviewx workflow with request ID ' + avx_id;
            log.insert();
            var message = responseBody["response"]["message"] + "Status : " + responseBody["response"]["status"];
            form.request_id = "R" + avx_id;
            form.appviewx_response = message;
            return "true";
        }
    } catch (error) {
        log.level = 'Error';
        log.script = 'new_certificate_request';
        log.application = 'AppViewX';
        log.message = 'Response for request with number ' + current.number + '\n ' + error;
        log.insert();
        current.comments = "Failure while processing the service request.";
        current.stage = "closed_incomplete";
        current.state = 4;
        reqRecord = current.request.getRefRecord();
        reqRecord.state = 4;
        reqRecord.request_state = "closed_incomplete";
        reqRecord.update();
        gs.addErrorMessage(error);
        return "false";
    }
})();

activity.result = (function() {
    try {
        var log = new GlideRecord('x_avx_appviewx_aut_logs');
        log.initialize();
        if (workflow.scratchpad.is_approval_needed_in_servicenow === "true") {
            current.comments = "Your certificate request has been approved.";
        }
		var client_id;
		var company = current.variables.company_name;
		workflow.info(gs.getUserName());
		if (gs.getProperty("glide.sys.domain.partitioning") === 'true') {
			var gr = new GlideRecordSecure('x_avx_appviewx_aut_connection_settings_table');
			gr.addQuery('company', company);
			gr.query();
			if (!gr.hasNext()) {
				throw "Could not find AppViewX instance url to fetch the details.";
			}
			while (gr.next()) {
				var url = gr.getValue('instance_url') || '';
				var mid_server = gr.getValue('mid_server') || '';
			}
		} else {
			url = gs.getProperty("x_avx_appviewx_aut.URL");
			mid_server = gs.getProperty("x_avx_appviewx_aut.MID Server Name");
		}
        var r = new sn_ws.RESTMessageV2();
        var script_include_call = new appviewx_oauth_token_generation();
        var token_generation = script_include_call.TestConnection(client_id, company);
        if (token_generation.length == 2 && token_generation[0] == 200) {
            var parse_response = JSON.parse(token_generation[1]);
            var token = parse_response['response'];
        } else {
            throw "Error while generating the Token.";
        }
        var form = current.variables;
        var upload_cert_content;
        if (form.csr_input_method == 'Upload CSR') {
            if (form.csr_upload_option == 'Paste CSR') {
                upload_cert_content = (form.paste_csr).toString();
            } else {
                upload_cert_content = (workflow.scratchpad.cert_content).toString();
            }
        }
        var san_values = [];
        workflow.scratchpad.add_dns_values = (form.add_dns_values).toString();
        workflow.scratchpad.add_ip_address = (form.add_ip_address).toString();
		workflow.scratchpad.add_email_address = (form.add_email_address).toString();
        if (workflow.scratchpad.add_dns_values === "true") {
            san_values.push("DNS");
        }
        if (workflow.scratchpad.add_ip_address === "true") {
            san_values.push("IP Address");
        }
        var cert_type;
        if (form.certificate_authority == 'DigiCert') {
            cert_type = (form.certificate_type).toString();
        } else if (form.certificate_authority == 'Entrust') {
            cert_type = (form.entrust_certificate_type).toString();
        } else if (form.certificate_authority == 'GoDaddy') {
            cert_type = (form.godaddy_certificate_type).toString();
        } else if (form.certificate_authority == 'Comodo Certificate Manager') {
            cert_type = (form.sectigo_certificate_type).toString();
        } else if (form.certificate_authority == 'GlobalSign') {
			cert_type = (form.globalsign_certificate_type).toString();
		} else if (form.certificate_authority == 'GlobalSignMSSL') {
			cert_type = (form.globalsign_mssl_certificate_type).toString();
		} else if (form.certificate_authority == 'Trustwave') {
			cert_type = (form.trustwave_certificate_type).toString();
		} else if (form.certificate_authority == 'HydrantID') {
			cert_type = (form.hydrantid_policy_name).toString();
		} else if (form.certificate_authority == 'SwissSign') {
			cert_type = (form.swisssign_product).toString();
		}
        var client_san;
        if (form.certificate_category == 'Client' && form.certificate_authority != 'Microsoft Enterprise') {
            san_values.push("Email Address");
            client_san = form.subject_alternative_name || '';
        } else if (form.certificate_authority == 'Microsoft Enterprise') {
			if (workflow.scratchpad.add_email_address === "true") {
				san_values.push("Email Address");
				client_san = form.san_email || '';
			}
        }
        log.level = 'Info';
        log.script = 'create_certificate';
        log.application = 'AppViewX';
        log.message = 'Triggering new certificate request API';
