answer = (function() {
    if (workflow.scratchpad.second_level_approver_selection_revoke === "Manager") {
        return "yes";
    } else if (workflow.scratchpad.second_level_approver_selection_enroll === "Manager" && workflow.scratchpad.use_approval_settings_from_create_for_revoke === "true") {
		return "yes";
	}
    return "no";
})();

answer = (function() {
    if (workflow.scratchpad.second_level_approver_selection_revoke === "Manager") {
        return "yes";
    } else if (workflow.scratchpad.second_level_approver_selection_enroll === "Manager" && workflow.scratchpad.use_approval_settings_from_create_for_revoke === "true") {
		return "yes";
	}
    return "no";
})();

activity.result = (function() {
    try {
        var reqRecord = current.request.getRefRecord();
        reqRecord.short_description = 'AppViewX Certificate Revocation';
        reqRecord.update();
        workflow.scratchpad.requestor_email = gs.getUser().getEmail();
        workflow.scratchpad.sys_id = current.sys_id;
        var ca_tag_name = current.variables.ca_tag_name;
        var gr = new GlideRecord('x_avx_appviewx_aut_clm_settings');
        gr.query('ca_tag_name', ca_tag_name);
        if (!gr.hasNext()) {
            throw "Could not find CA Tag with the name - " + ca_tag_name + " in the CLM settings table";
        }
        while (gr.next()) {
            workflow.scratchpad.workflow_name_for_revoke_appviewx =
                gr.workflow_name_for_revoke_appviewx;
            //for revoke
            workflow.scratchpad.is_approval_needed_in_servicenow_revoke = gr.is_approval_needed_in_servicenow_revoke;
            workflow.scratchpad.approval_levels_in_servicenow_revoke = gr.approval_levels_in_servicenow_revoke;
            workflow.scratchpad.create_catalog_task_ticket = gr.create_catalog_task_ticket;
            workflow.scratchpad.task_priority = gr.task_priority || '';
			workflow.scratchpad.task_assignment_group = gr.task_assignment_group || '';
            workflow.scratchpad.task_assigned_to = gr.task_assigned_to || '';
            var ticket_assignment_group = gr.ticket_assignment_group || '';
            var ticket_assignment_group_name = gr.ticket_assignment_group_name || '';
            workflow.scratchpad.use_approval_settings_from_create_for_revoke = gr.use_approval_settings_from_create_for_revoke;
            workflow.scratchpad.servicenow_approver_revoke = gr.approver_selection_revoke;
			workflow.scratchpad.second_level_approver_selection_revoke = gr.revoke_approver_selection_two;
            if (gr.approver_selection_revoke == 'Assignment Group') {
                workflow.scratchpad.user_group_name_revoke = gr.assignment_group_revoke;
            }
            if (gr.revoke_approver_selection_two == 'Assignment Group') {
                workflow.scratchpad.second_level_approval_group_revoke = gr.revoke_user_group_snow_two;
            }
            //for enroll  
            workflow.scratchpad.is_approval_required_in_servicenow_enroll = gr.is_approval_required_in_servicenow_enroll;
            workflow.scratchpad.approval_levels_in_servicenow_create = gr.approval_levels_in_servicenow;
            workflow.scratchpad.servicenow_approver_enroll = gr.approver_selection;
			workflow.scratchpad.second_level_approver_selection_enroll = gr.enroll_approver_selection_two;
            if (gr.approver_selection == 'Assignment Group') {
                workflow.scratchpad.user_group_name_enroll = gr.assignment_group;
            }
            if (gr.enroll_approver_selection_two == 'Assignment Group') {
                workflow.scratchpad.second_level_approval_group_enroll = gr.enroll_user_group_approval_two;
            }

        }
		var gr_sys_user = new GlideRecord('sys_user');
		gr_sys_user.get(gs.getUserID());
		workflow.scratchpad.manager_id = gr_sys_user.manager;
		var company = current.variables.company_name;
		if (gs.getProperty("glide.sys.domain.partitioning") === 'true') {
			gr = new GlideRecordSecure('x_avx_appviewx_aut_connection_settings_table');
			gr.addQuery('company', company);
			gr.query();
			if (!gr.hasNext()) {
				throw "Could not find AppViewX instance url to fetch the details.";
			}
			while (gr.next()) {
				workflow.scratchpad.cmdb_sync = gr.getDisplayValue('enable_appviewx_inventory_sync') || 'false';
			}
		} else {
			workflow.scratchpad.cmdb_sync = gs.getProperty("x_avx_appviewx_aut.CMDB Sync");
		}
        var myGroups = gs.getUser().getMyGroups().toString().split(",");
        if (ticket_assignment_group == "Allow user to select assignment group") {
            workflow.scratchpad.ticket_assignment_group_value = current.variables.ticket_assignment_group;
        } else if (ticket_assignment_group == "Use assignment group from clm settings") {
            workflow.scratchpad.ticket_assignment_group_value = ticket_assignment_group_name;
        } else if (ticket_assignment_group == "Use logged-in user assignment group") {
            workflow.scratchpad.ticket_assignment_group_value = myGroups[0];
        }
        workflow.info(workflow.scratchpad.ticket_assignment_group_value);
        if (ticket_assignment_group != "None of the group") {
            current.assignment_group = workflow.scratchpad.ticket_assignment_group_value;
        }
        workflow.scratchpad.ticket_assignment_group = ticket_assignment_group;
        return "true";
    } catch (e) {
        var log = new GlideRecord('x_avx_appviewx_aut_logs');
        log.initialize();
        log.level = 'Error';
        log.script = 'Setting scratchpad values revoke.';
        log.application = 'AppViewX';
        log.message = 'Error occured while generating the scratchpad values revoke ' + e;
        log.insert();
        current.state = 4;
        current.stage = "closed_incomplete";
        current.comments = "Failure while processing the service request.";
        reqRecord = current.request.getRefRecord();
        // Set the values on the request table
        reqRecord.state = 4;
        reqRecord.request_state = 'closed_incomplete';
        reqRecord.update();
        workflow.error("Error While Processing the Workflow " + e);
        return "false";
    }

})();

