answer = (function() {
	workflow.info(workflow.scratchpad.approval_levels_in_servicenow_create);
	workflow.info(workflow.scratchpad.use_approval_settings_from_create_for_revoke);
    // set approver mail
    if (workflow.scratchpad.use_approval_settings_from_create_for_revoke === "true") {
        if (workflow.scratchpad.is_approval_required_in_servicenow_enroll === "true") {
            if (workflow.scratchpad.servicenow_approver_enroll == 'Manager') {
                var gr_sys_user = new GlideRecord('sys_user');
                gr_sys_user.get(gs.getUserID());
                workflow.scratchpad.servicenow_manager_id = gr_sys_user.manager.user_name;
                manager_email = gr_sys_user.manager.email;
                workflow.scratchpad.servicenow_approver_mail = manager_email;
            } else {
                var user_group_mail;
                var gr_user_group = new GlideRecord('sys_user_group');
                gr_user_group.addQuery('sys_id', workflow.scratchpad.user_group_name_enroll);
                gr_user_group.query();
                if (!gr_user_group.hasNext()) {
                    throw "Could not find the given user group in the user table";
                }
                while (gr_user_group.next()) {
                    user_group_mail = gr_user_group.getValue('email');
                }
                workflow.scratchpad.servicenow_usergroup = workflow.scratchpad.user_group_name_enroll;
                workflow.scratchpad.servicenow_approver_mail = user_group_mail;
            }
			workflow.scratchpad.second_level_user_group_approval = workflow.scratchpad.second_level_approval_group_enroll;
            return "yes";
        }
        return "no";
    } else {
        if (workflow.scratchpad.is_approval_needed_in_servicenow_revoke == "true") {
            if (workflow.scratchpad.servicenow_approver_revoke == 'Manager') {
                var gr_sys_user_revoke = new GlideRecord('sys_user');
                gr_sys_user_revoke.get(gs.getUserID());
                workflow.scratchpad.servicenow_manager_id = gr_sys_user_revoke.manager.user_name;
                manager_email = gr_sys_user_revoke.manager.email;
                workflow.scratchpad.servicenow_approver_mail = manager_email;
            } else {
                var user_group_mail_revoke;
                var gr_user_group_revoke = new GlideRecord('sys_user_group');
                gr_user_group_revoke.addQuery('sys_id', workflow.scratchpad.user_group_name_revoke);
                gr_user_group_revoke.query();
                if (!gr_user_group_revoke.hasNext()) {
                    throw "Could not find the given user group in the user table";
                }
                while (gr_user_group_revoke.next()) {
                    user_group_mail_revoke = gr_user_group_revoke.getValue('email');
                }
                workflow.scratchpad.servicenow_usergroup = workflow.scratchpad.user_group_name_revoke;
                workflow.scratchpad.servicenow_approver_mail = user_group_mail_revoke;
            }
			workflow.scratchpad.second_level_user_group_approval = workflow.scratchpad.second_level_approval_group_revoke;
            return "yes";
        }
        return "no";
    }

})();

