current.state = 4;
current.stage = "closed_incomplete";
var reqRecord = current.request.getRefRecord();
// Set the values on the request table
reqRecord.state = 4;
reqRecord.request_state = "closed_incomplete";
reqRecord.update();
var log = new GlideRecord('x_avx_appviewx_aut_logs');
log.initialize();
log.level = 'Error';
log.script = 'Approval rejected Revoke';
log.application = 'AppViewX';
log.message = 'ServiceNow approval for certificate revocation is rejected by your approver';
log.insert();

var answer = [];
current.comments = "Your renew certificate request is pending for second level approval in ServiceNow.";
answer.push(workflow.scratchpad.second_level_user_group_approval);

// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the overall approval status for this approval.
