var answer = [];
current.comments = "Your renew certificate request is pending for the second level approval in ServiceNow.";
answer.push(workflow.scratchpad.manager_id);

var scTask = new GlideRecord('sc_task');
scTask.addQuery('parent', current.sys_id);
scTask.query();

while (scTask.next()) {
    var state_value = scTask.state.getDisplayValue();
}
if (state_value === 'Closed Complete') {
    scTask = new GlideRecord('sc_task');
    scTask.addQuery('parent', current.sys_id);
    scTask.query();
    while (scTask.next()) {
        scTask.work_notes = "Certificate revocation has been successfully completed.";
        scTask.update();
    }
    current.state = 3;
    current.stage = "complete";
} else {
    scTask = new GlideRecord('sc_task');
    scTask.addQuery('parent', current.sys_id);
    scTask.query();
    while (scTask.next()) {
        scTask.work_notes = "Certificate revocation is failed in AppViewX.";
        scTask.update();
    }
    current.state = 4;
    current.stage = "closed_incomplete";
}

