    current.stage = "closed_incomplete";
}

activity.result = (function() {
try {
    var log = new GlideRecord('x_avx_appviewx_aut_logs');
    log.initialize();
    if (workflow.scratchpad.is_approval_needed_in_servicenow_revoke === "true" ) {
        current.comments = "Your certificate revoke request has been approved.";
    }
    if (workflow.scratchpad.use_approval_settings_from_create_for_revoke === "true" && workflow.scratchpad.is_approval_required_in_servicenow_enroll === "true"){
		current.comments = "Your certificate revoke request has been approved.";
	}
	var client_id;
	var company = current.variables.company_name;
	if (gs.getProperty("glide.sys.domain.partitioning") === 'true') {
		var gr = new GlideRecordSecure('x_avx_appviewx_aut_connection_settings_table');
		gr.addQuery('company', company);
		gr.query();
		if (!gr.hasNext()) {
			throw "Could not find AppViewX instance url to fetch the details.";
		}
		while (gr.next()) {
			var url = gr.getValue('instance_url') || '';
			var mid_server = gr.getValue('mid_server') || '';
		}
	} else {
		url = gs.getProperty("x_avx_appviewx_aut.URL");
		mid_server = gs.getProperty("x_avx_appviewx_aut.MID Server Name");
	}
    var r = new sn_ws.RESTMessageV2();
    var script_include_call = new appviewx_oauth_token_generation();
    var token_generation = script_include_call.TestConnection(client_id, company);
    if (token_generation.length == 2 && token_generation[0] == 200) {
        var parse_response = JSON.parse(token_generation[1]);
        var token = parse_response['response'];
    } else {
        throw "Error While Generating the token.";
    }
    var form = current.variables;
    log.level = 'Info';
    log.script = 'revoke_certificate';
    log.application = 'AppViewX';
    log.message = 'Triggerring revoke request API';
    log.insert();
    var payload = {
        "payload": {
            "header": {
                "workflowName": (workflow.scratchpad.workflow_name_for_revoke_appviewx || '').toString()
            },
            "data": {
                "input": {
                    "requestData": [{
                        "sequenceNo": 1,
                        "scenario": "scenario",
                        "fieldInfo": {
                            "common_name": (form.common_name || '').toString(),
                            "serial_number": (form.serial_number || '').toString(),
                            "reason": (form.reason || '').toString(),
                            "comments": (form.comments || '').toString(),
                            "email_id": (form.email_id || '').toString(),
                            "ritm_number": (current.number || '').toString(),
                            "appviewx_approval": (form.appviewx_approval || '').toString(),
                            "one_level_mail_id": (form.appviewx_one_level_approval_email_id || '').toString(),
                            "two_level_mail_id": (form.appviewx_two_level_approval_email_id || '').toString(),
                            "sys_id": (workflow.scratchpad.sys_id || '').toString(),
                            "req_number": (current.request.getDisplayValue() || '').toString(),
                            "req_sys_id": (current.request || '').toString(),
                            "requestor_id": (workflow.scratchpad.requestor_email || '').toString(),
							"cmdb_sync": (workflow.scratchpad.cmdb_sync || '').toString(),
							"catalog_task": (workflow.scratchpad.create_catalog_task_ticket || '').toString()
                        }
                    }]
                },
                "globalData": {},
                "task_action": 1
            }
        }
    };
	workflow.info(JSON.stringify(payload));
	workflow.info(url + "/avxapi/visualworkflow-submit-request?gwsource=web");
	if (mid_server != "") {	r.setMIDServer(mid_server); r.setEccParameter('skip_sensor','true'); }
    r.setHttpMethod("post");
    r.setRequestHeader("token", token);
    r.setRequestHeader('User-Agent', 'snow');
    r.setRequestHeader("Content-Type", "application/json");
    r.setEndpoint(url + "/avxapi/visualworkflow-submit-request?gwsource=web");
    r.setRequestBody(JSON.stringify(payload));
	r.setHttpTimeout(30000);
    var response = r.executeAsync();
    var reqRecord = current.request.getRefRecord();
    if (response.haveError()) {
        var responseMessage = response.getErrorMessage() || '';
        current.comments = "Failure while processing the service request.";
        var responseBody = response.getBody() || '';
        log.level = 'Error';
        log.script = 'revoke_certificate_request';
        log.application = 'AppViewX';
        log.message = 'Revoke certificate request failed ' + responseBody + '\nMessage: ' + responseMessage;
        log.insert();
        responseBody = JSON.parse(responseBody);
        form.request_id = "Failed to submit revoke certificate request";
        form.appviewx_response = responseBody["message"];
        current.comments = responseBody["message"];
        current.state = 4;
		current.stage = "closed_incomplete";
        // Set the values on the request table
        reqRecord.state = 4;
        reqRecord.request_state = "closed_incomplete";
        reqRecord.update();
		return "false";
    } else {
        responseBody = response.getBody();
        responseBody = JSON.parse(responseBody);
        var avx_id = responseBody["response"]["requestId"];
        log.level = 'Info';
        log.script = 'revoke_certificate_request';
        log.application = 'AppViewX';
        log.message = 'Successfully triggered revoke appviewx workflow with request ID ' + avx_id;
        log.insert();
        var message = responseBody["response"]["message"] + "Status : " + responseBody["response"]["status"];
        current.comments = "Triggered appviewx workflow with Request ID " + avx_id;
        form.request_id = "R" + avx_id;
        form.appviewx_response = message;
		return "true";
    }
} catch (error) {
    log.level = 'Error';
    log.script = 'revoke_certificate_request';
    log.application = 'AppViewX';
    log.message = 'Response for request with number ' + form.number + '\n ' + responseBody;
    log.insert();
    current.comments = "Failure while processing the service request.";
    current.state = 4;
	current.stage = "closed_incomplete";
	reqRecord = current.request.getRefRecord();
    reqRecord.state = 4;
	reqRecord.request_state = "closed_incomplete";
    reqRecord.update();
    gs.addErrorMessage(error);
	return "false";
}
})();

