sys_variable_value.value:
activity.result = (function() {
    try {
        var cert_attr = [];
        var cert_attributes = {};
		var company = current.variables.company_name;
        cert_attributes['attribute'] = 'requestor';
		cert_attributes['attribute_value'] = (workflow.scratchpad.requestor_id || '');
		cert_attr.push(cert_attributes);
		cert_attributes = {};
		cert_attributes['attribute'] = 'requestor_assignment_group';
		cert_attributes['attribute_value'] = (workflow.scratchpad.assignment_group_name || '');
		cert_attr.push(cert_attributes);
        var gr = new GlideRecord('x_avx_appviewx_aut_clm_settings');
        gr.addQuery('ca_tag_name', current.variables.ca_tag_name);
		if (gs.getProperty("glide.sys.domain.partitioning") === 'true') {
			gr.addQuery('company', company);
		}
        gr.query();
        var clm_cert_attribute;
        while (gr.next()) {
            clm_cert_attribute = gr.getValue('meta_data_configured_in_appviewx');
        }
        var meta_info = JSON.parse(clm_cert_attribute) || {};
        var meta_length = Object.keys(meta_info).length;
        if (meta_length > 0) {
            var obj = [];
            for (var key in meta_info) {
                var key_value = meta_info[key];
                obj.push(key_value);
            }
            for (var i = 1; i < meta_length + 1; i++) {
                cert_attributes = {};
                var field_name = "meta_data_" + (i.toString());
                cert_attributes['attribute'] = obj[i - 1];
				cert_attributes['attribute_value'] = (current.variables[field_name] || '').toString();
				cert_attr.push(cert_attributes);
            }
        }
        workflow.scratchpad.certificate_attributes_update = JSON.stringify(cert_attr);
        return "true";
    } catch (error) {
        current.comments = 'Failure while processing the service request.';
		current.stage = "closed_incomplete";
        current.state = 4;
        var reqRecord = current.request.getRefRecord();
        // Set the values on the request table
        reqRecord.state = 4;
        reqRecord.request_state = "closed_incomplete";
        reqRecord.update();
        var log = new GlideRecord('x_avx_appviewx_aut_logs');
        log.initialize();
        log.level = 'Error';
        log.script = 'Certificate attribute generation';
        log.application = 'AppViewX';
        log.message = 'Error while generating the certificate generation ' + error;
        log.insert();
        gs.addErrorMessage('Error while generating the cert attributes');
        return "false";
    }
})();

