        log.initialize();
        log.level = 'Error';
        log.script = 'Setting scratchpad values renew.';
        log.application = 'AppViewX';
        log.message = 'Error occured while generating the scratchpad values renew ' + e;
        log.insert();
        current.comments = "Failure while processing the service request.";
        current.state = 4;
        current.stage = "closed_incomplete";
        reqRecord = current.request.getRefRecord();
        // Set the values on the request table
        reqRecord.state = 4;
        reqRecord.request_state = 'closed_incomplete';
        reqRecord.update();
        workflow.error("Error While Processing the Renew Workflow " + e);
        return "false";
    }

})();

