activity.result = (function() {
    try {
        var log = new GlideRecord('x_avx_appviewx_aut_logs');
        log.initialize();
        if (workflow.scratchpad.is_approval_needed_in_servicenow_renew === "true") {
            current.comments = "Your certificate renewal request has been approved.";
        }
        if (workflow.scratchpad.use_approval_settings_from_create_for_renew === "true" && workflow.scratchpad.is_approval_required_in_servicenow_enroll === "true") {
            current.comments = "Your certificate renewal request has been approved.";
        }
		var client_id;
		var company = current.variables.company_name;
		if (gs.getProperty("glide.sys.domain.partitioning") === 'true') {
			var gr = new GlideRecordSecure('x_avx_appviewx_aut_connection_settings_table');
			gr.addQuery('company', company);
			gr.query();
			if (!gr.hasNext()) {
				throw "Could not find AppViewX instance url to fetch the details.";
			}
			while (gr.next()) {
				var url = gr.getValue('instance_url') || '';
				var mid_server = gr.getValue('mid_server') || '';
			}
		} else {
			url = gs.getProperty("x_avx_appviewx_aut.URL");
			mid_server = gs.getProperty("x_avx_appviewx_aut.MID Server Name");
		}
        var r = new sn_ws.RESTMessageV2();
        var script_include_call = new appviewx_oauth_token_generation();
        var token_generation = script_include_call.TestConnection(client_id, company);
        if (token_generation.length == 2 && token_generation[0] == 200) {
            var parse_response = JSON.parse(token_generation[1]);
            var token = parse_response['response'];
        } else {
            throw "Error While Generating the token.";
        }
        var form = current.variables;
        var upload_cert_content;
        if (form.csr_type == "Upload New CSR") {
            if (form.csr_upload_option == 'Paste CSR') {
                upload_cert_content = (form.paste_csr).toString();
            } else {
                upload_cert_content = (workflow.scratchpad.cert_content).toString();
            }
        }
        log.level = 'Info';
        log.script = 'renew_certificate';
        log.application = 'AppViewX';
        log.message = 'Triggerring renew request API';
        log.insert();
        var payload = {
            "payload": {
                "header": {
                    "workflowName": (workflow.scratchpad.workflow_name_for_renew_appviewx || '').toString()
                },
                "data": {
                    "input": {
                        "requestData": [{
                            "sequenceNo": 1,
                            "scenario": "scenario",
                            "fieldInfo": {
                                "certificate_category": (form.certificate_type || '').toString(),
                                "common_name": (form.common_name || '').toString(),
                                "serial_number": (form.serial_number || '').toString(),
                                "cert_format": ((form.certificate_issuance_format || '').toString()).trim(),
                                "cert_issue_mode": (workflow.scratchpad.certificate_retrieval_mode || '').toString(),
                                "renew_method": (form.renwal_type || '').toString(),
                                "upload_csr": (upload_cert_content || '').toString(),
                                "csr_type": (form.csr_type || '').toString(),
                                "email_id": (form.email_id || '').toString(),
                                "requestor_id": (workflow.scratchpad.requestor_id || '').toString(),
                                "ritm_number": (current.number || '').toString(),
                                "sys_id": (workflow.scratchpad.sys_id || '').toString(),
                                "req_number": (current.request.getDisplayValue() || '').toString(),
                                "req_sys_id": (current.request || '').toString(),
                                "appviewx_approval": (workflow.scratchpad.avx_approval || '').toString(),
                                "one_level_mail_id": (workflow.scratchpad.avx_one_level || '').toString(),
                                "two_level_mail_id": (workflow.scratchpad.avx_two_level || '').toString(),
                                "certificate_authority": (form.certificate_authority || '').toString(),
                                "appviewx_url": url.toString(),
                                "cmdb_sync": (workflow.scratchpad.cmdb_sync || '').toString(),
                                "push_to_end_device": (form.push_to_end_device || '').toString(),
                                "push_assignment_group": (workflow.scratchpad.push_assignment_group || '').toString(),
                                "send_certificate": (workflow.scratchpad.send_certificate_to_end_user || '').toString(),
								"time_zone": (workflow.scratchpad.time_zone || '').toString(),
								"catalog_task": (workflow.scratchpad.create_catalog_task_ticket || '').toString(),
								"form_cert_attributes": (JSON.parse(workflow.scratchpad.certificate_attributes_update) || []),
								"requestor_assignment_group": (workflow.scratchpad.assignment_group_name || '').toString(),
								"form_assignment_group": (form.ticket_assignment_group.name || '').toString(),
								"zip_certificate_file": (workflow.scratchpad.zip_certificate_file || false).toString(),
								"zip_trust_certificate": (workflow.scratchpad.trust_cert_file || false).toString()
                            }
                        }]
                    },
                    "globalData": {},
                    "task_action": 1
                }
            }
        };
        workflow.info(JSON.stringify(payload));
        if (mid_server != "") {
            r.setMIDServer(mid_server);
            r.setEccParameter('skip_sensor', 'true');
        }
        r.setHttpMethod("post");
        r.setRequestHeader("token", token);
        r.setRequestHeader('User-Agent', 'snow');
        r.setRequestHeader("Content-Type", "application/json");
        r.setEndpoint(url + "/avxapi/visualworkflow-submit-request?gwsource=web");
        r.setRequestBody(JSON.stringify(payload));
        r.setHttpTimeout(30000);
        var response = r.executeAsync();
        var reqRecord = current.request.getRefRecord();
        if (response.haveError()) {
            var responseMessage = response.getErrorMessage() || '';
            var responseBody = response.getBody() || '';
            log.level = 'Error';
            log.script = 'renew_certificate_request';
            log.application = 'AppViewX';
            log.message = 'Renew certificate request failed ' + responseBody + '\nMessage: ' + responseMessage;
            log.insert();
            responseBody = JSON.parse(responseBody);
            form.request_id = "Failed to submit renew certificate request";
            form.appviewx_response = responseBody["message"];
			current.comments = "Error while triggering the renew appviewx workflow " + responseBody["message"];
            current.comments = "Failure while processing the service request.";
            current.state = 4;
            current.stage = "closed_incomplete";
            // Set the values on the request table
            reqRecord.state = 4;
            reqRecord.request_state = "closed_incomplete";
            reqRecord.update();
            return "false";
        } else {
            responseBody = response.getBody();
            responseBody = JSON.parse(responseBody);
            var avx_id = responseBody["response"]["requestId"];
            log.level = 'Info';
            log.script = 'renew_certificate_request';
            log.application = 'AppViewX';
            log.message = 'Successfully triggered renew appviewx workflow with request ID ' + avx_id;
            log.insert();
            var message = responseBody["response"]["message"] + "Status : " + responseBody["response"]["status"];
            current.comments = "Triggered appviewx workflow with Request ID " + avx_id;
            form.request_id = "R" + avx_id;
            form.appviewx_response = message;
            return "true";
        }
    } catch (error) {
        log.level = 'Error';
        log.script = 'renew_certificate_request';
        log.application = 'AppViewX';
        log.message = 'Response for request with number ' + form.number + '\n ' + responseBody;
        log.insert();
        current.comments = "Failure while processing the service request.";
        current.state = 4;
        current.stage = "closed_incomplete";
        reqRecord = current.request.getRefRecord();
        reqRecord.state = 4;
        reqRecord.request_state = "closed_incomplete";
        reqRecord.update();
        gs.addErrorMessage(error);
        return "false";
    }
})();

